import React, { useEffect, useRef } from 'react';
import ArrowForwardIosRoundedIcon from '@mui/icons-material/ArrowForwardIosRounded';
import ArrowBackIosNewRoundedIcon from '@mui/icons-material/ArrowBackIosNewRounded';

function Content() {
    const carouselRef = useRef(null);

    const willLeftLessThan40pxToScrollEnd = (nextStep) => {
        const carousel = carouselRef.current;
        const scrollLeftAfterTwoClicks = carousel.scrollLeft + (nextStep * 2)
        return scrollLeftAfterTwoClicks > carousel.scrollWidth - 40
    }

    const willLeftLessThan40pxToScrollStart = (nextStep) => {
        const carousel = carouselRef.current;
        const scrollLeftAfterOneClick = carousel.scrollLeft - nextStep
        return scrollLeftAfterOneClick < 40
    }

    const handleClickGoAhead = () => {
        const carousel = carouselRef.current;
        let nextStep = carousel.offsetWidth;
        if (willLeftLessThan40pxToScrollEnd(nextStep)) nextStep *= 2;

        carousel.scroll({
            left: carousel.scrollLeft + nextStep,
            behavior: "smooth"
        })
    }

    const handleClickGoBack = () => {
        const carousel = carouselRef.current;
        let nextStep = carousel.offsetWidth;
        if (willLeftLessThan40pxToScrollStart(nextStep)) nextStep *= 2;

        carousel.scroll({
            left: carousel.scrollLeft - nextStep,
            behavior: "smooth"
        })
    }

    useEffect(() => {
        const cards = document.querySelectorAll(".card");

        cards.forEach((card) => {
            const cardHeading = card.querySelector(".card__heading");
            const cardInfo = card.querySelector(".card__info");

            cardInfo.style.transform = `translateY(calc(100% - ${cardHeading.offsetHeight}px))`;

            card.addEventListener("mouseover", () => {
                cardInfo.style.transform = "translateY(0)";
            });

            card.addEventListener("mouseout", () => {
                cardInfo.style.transform = `translateY(calc(100% - ${cardHeading.offsetHeight}px))`;
            });
        });
    }, []);

    return (
        <div className="grid">
            <div className="grid__item grid__item_item4">
                <div id="wrapper">
                    <div id="carousel" ref={carouselRef}>
                        <div className="container">
                            <div className="img-container">
                                <span className="trending">Trending</span>
                                <img src="https://images.unsplash.com/photo-1534809027769-b00d750a6bac?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1534&q=80" alt="" />
                            </div>
                            <ul className="social-media">
                                <li><a href="#"><i className="fa fa-facebook"></i></a></li>
                                <li><a href="#"><i className="fa fa-twitter"></i></a></li>
                            </ul>
                            <div className="user-info">
                                <h2>Aniket Singh</h2>
                                <span>20-july</span>
                            </div>
                        </div>
                        {/* Repeat other containers as per your requirements */}
                    </div>
                </div>
                <div className="buttons">
                    <button onClick={handleClickGoBack}>
                        <ArrowBackIosNewRoundedIcon />
                    </button>
                    <button onClick={handleClickGoAhead}>
                        <ArrowForwardIosRoundedIcon />
                    </button>
                </div>
            </div>
            <div className="grid__item grid__item_item5">four columns</div>
        </div>
    );
}

export default Content;
