import { useState, useEffect } from 'react';

function NavBar() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeLink, setActiveLink] = useState("home");

  useEffect(() => {
    const toggle = document.getElementById("header-toggle");
    const nav = document.getElementById("nav-menu");

    if (toggle && nav) {
      const handleToggle = () => {
        setMenuOpen((prev) => !prev);
      };

      toggle.addEventListener("click", handleToggle);

      return () => {
        toggle.removeEventListener("click", handleToggle);
      };
    }
  }, []);

  return (
    <header className="header">
      <a href="#" className="header-logo">Clay</a>

      <div className="header-toggle">
        <i className={`bx ${menuOpen ? "bx-x" : "bx-menu"}`} id="header-toggle"></i>
      </div>

      <nav className={`nav ${menuOpen ? "show" : ""}`} id="nav-menu">
        <div className="nav-content bd-grid">
          <a href="#" className="nav-perfil">
            <div className="nav-img">
              <img src="https://i.postimg.cc/Z5VcmyV0/perfil.jpg" alt="perfil" />
            </div>

            <div>
              <span className="nav-name">Clay</span>
              <span className="nav-name">Mitchell</span>
            </div>
          </a>

          <div className="nav-menu">
            <ul className="nav-list">
              {[
                { name: "Home", id: "home" },
                { name: "About", id: "about" },
                { name: "Skills", id: "skills" },
                { name: "Works", id: "works" },
                { name: "Contact", id: "contact" },
              ].map((item) => (
                <li key={item.id} className="nav-item">
                  <a
                    href="#"
                    className={`nav-link ${activeLink === item.id ? "active" : ""}`}
                    onClick={() => setActiveLink(item.id)}
                  >
                    {item.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
}

export default NavBar;
