import React, { useEffect, useState } from 'react';
import axios from 'axios'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { Paper, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit'
import TextField from '@mui/material/TextField';
import TablePagination from '@mui/material/TablePagination';
import Box from '@mui/material/Box';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import Grid from '@mui/material/Grid';
import Autocomplete from '@mui/material/Autocomplete';
import Checkbox from '@mui/material/Checkbox';
import CheckBoxOutlineBlankIcon from '@mui/icons-material/CheckBoxOutlineBlank';
import CheckBoxIcon from '@mui/icons-material/CheckBox';
const icon = <CheckBoxOutlineBlankIcon fontSize="small" />;
const checkedIcon = <CheckBoxIcon fontSize="small" />;

function TableMovie() {
    const [movies, setMovie] = useState(null)
    const [moviesle, setMovieLe] = useState(null)
    const [moviesbo, setMovieBo] = useState(null)
    const [deleteId, setDeleteId] = useState(null)
    const [confirmOpen, setConfirmOpen] = useState(false)
    const [openEdit, setOpenEdit] = useState(false)
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [newMovie, setNewMovie] = useState({})

    const [types, setType] = useState(null)
    const [selectedType, setSelectedType] = useState([]);

    // const [snackbarOpen, setSnackbarOpen] = useState(false)

    const [value, setValue] = React.useState('1');

    const handleChangeTab = (event, newValue) => {
        setValue(newValue);
    };


    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const [editMovie, setEditMovie] = useState({})

    // const handleClickOpenEdit = (movie) => {
    //     setEditMovie(movie)
    //     setOpenEdit(true)
    // }
    const handleClickOpenEdit = async (id) => {
        try {
            // Gọi API để lấy dữ liệu phim
            const response = await fetch(`http://localhost:8080/movie/${id}`);
            const movieData = await response.json();

            if (!movieData || !Array.isArray(movieData.types)) {
                console.error("Dữ liệu movie hoặc types không hợp lệ", movieData);
                return;
            }

            // Lọc danh sách types đã chọn dựa vào danh sách types có sẵn
            const selectedTypes = Array.isArray(types)
                ? types.filter(type => movieData.types.some(t => t.id === type.id))
                : [];

            setEditMovie(movieData);
            setSelectedType(selectedTypes);
            setOpenEdit(true);
        } catch (error) {
            console.error("Lỗi khi lấy movie data:", error);
        }
    };



    const handleConfirmOpen = (id) => {
        setDeleteId(id)
        setConfirmOpen(true)
    }
    const handleConfirmClose = (id) => {
        setDeleteId(null)
        setConfirmOpen(false)
    }
    const handleCloseEdit = () => {
        setOpenEdit(false)
    }

    const handleChangeEdit = (event) => {
        setEditMovie({
            ...editMovie,
            [event.target.name]: event.target.value
        });
    };


    // delete
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/website/movie/${id}`);
            setMovie(moviesle.filter(movie => movie.id !== id));
            handleConfirmClose()
        } catch (error) {
            alert('There was an error deleting the style')
        }
    };


    // Update
    const handleEditMovie = async () => {
        try {

            const response = await axios.put('http://localhost:8080/website/movie/${editMovie.id}', editMovie);

            setMovie(prevMovies =>
                prevMovies.map(movie =>
                    movie.id === editMovie.id ? response.data : movie
                )
            );

            handleCloseEdit();
        } catch (error) {
            console.error('Lỗi xảy ra khi cập nhật:', error);
        }
    };


    useEffect(() => {
        axios.get('http://localhost:8080/website/movieLe')
            .then(response => {
                setMovieLe(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    useEffect(() => {
        axios.get('http://localhost:8080/website/movieBo')
            .then(response => {
                setMovieBo(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    return (
        <Box sx={{ '& > :not(movie)': { maxWidth: '100%' } }}>

            <TabContext value={value}>
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                    <TabList onChange={handleChangeTab} aria-label="lab API tabs example">
                        <Tab label="Phim lẻ" value="1" />
                        <Tab label="Phim bộ" value="2" />

                    </TabList>
                </Box>
                <TabPanel value="1">
                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow style={{ backgroundColor: 'Gray' }}>
                                    <TableCell align="center">STT</TableCell>
                                    <TableCell align="center">Name Movie</TableCell>
                                    <TableCell align="center">Duration</TableCell>
                                    <TableCell align="center">Type</TableCell>
                                    <TableCell align="center">Hình ảnh</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {Array.isArray(moviesle) && moviesle.length > 0 ? (
                                    moviesle.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((moviele, index) => (
                                        <TableRow key={moviele.id}>
                                            <TableCell align="center">{page * rowsPerPage + index + 1}</TableCell>
                                            <TableCell align="center">{moviele.name}</TableCell>
                                            <TableCell align="center">{moviele.duration} (phút)</TableCell>
                                            <TableCell align="center"> {moviele.types.map((type) => type.name).join(", ")}</TableCell>
                                            <TableCell align="center">
                                                <img
                                                    src={`data:image/jpeg;base64,${moviele.image}`}
                                                    alt={moviele.name}
                                                    style={{ width: "50px", height: "50px", objectFit: "cover", borderRadius: "8px" }}
                                                />
                                            </TableCell>
                                            <TableCell sx={{ display: 'flex', justifyContent: 'center' }}>
                                                <IconButton color="primary" onClick={() => handleConfirmOpen(moviele.id)}>
                                                    <DeleteIcon />
                                                </IconButton>
                                                <IconButton color="primary" onClick={() => handleClickOpenEdit(moviele)}>
                                                    <EditIcon />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                ) : (
                                    <TableRow >
                                        <TableCell colSpan={6} align="center">Loading...</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 10, 25]}
                            component="div"
                            count={movies != null ? movies.length : 0}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </TableContainer>
                </TabPanel>
                <TabPanel value="2">
                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow style={{ backgroundColor: 'Gray' }}>
                                    <TableCell align="center">STT</TableCell>
                                    <TableCell align="center">Name Movie</TableCell>
                                    <TableCell align="center">Duration</TableCell>
                                    <TableCell align="center">Episodes</TableCell>
                                    <TableCell align="center">Type</TableCell>
                                    <TableCell align="center">Hình ảnh</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {Array.isArray(moviesbo) && moviesbo.length > 0 ? (
                                    moviesbo.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((moviebo, index) => (
                                        <TableRow key={moviebo.id}>
                                            <TableCell align="center">{page * rowsPerPage + index + 1}</TableCell>
                                            <TableCell align="center">{moviebo.name}</TableCell>
                                            <TableCell align="center">{moviebo.duration} (phút)</TableCell>
                                            <TableCell align="center">{moviebo.episodes} (tập)</TableCell>
                                            <TableCell align="center"> {moviebo.types.map((type) => type.name).join(", ")}</TableCell>
                                            <TableCell align="center">
                                                <img
                                                    src={`data:image/jpeg;base64,${moviebo.image}`}
                                                    alt={moviebo.name}
                                                    style={{ width: "50px", height: "50px", objectFit: "cover", borderRadius: "8px" }}
                                                />
                                            </TableCell>
                                            <TableCell sx={{ display: 'flex', justifyContent: 'center' }}>
                                                <IconButton color="primary" onClick={() => handleConfirmOpen(moviebo.id)}>
                                                    <DeleteIcon />
                                                </IconButton>
                                                <IconButton color="primary" onClick={() => handleClickOpenEdit(moviebo)}>
                                                    <EditIcon />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                ) : (
                                    <TableRow>
                                        <TableCell colSpan={6} align="center">Loading...</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 10, 25]}
                            component="div"
                            count={movies != null ? movies.length : 0}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </TableContainer>
                </TabPanel>

            </TabContext>



            {/* Xoa */}
            <Dialog open={confirmOpen} onClose={handleConfirmClose}>
                <DialogTitle>Confirm DeleteId</DialogTitle>
                <DialogContent>Are you sure you want to delete this Style?</DialogContent>
                <DialogActions>
                    <Button onClick={handleConfirmClose} color='primary'>
                        Cancel
                    </Button>
                    <Button onClick={() => { handleDelete(deleteId); }} color='secondary' variant='contained'>
                        Delete
                    </Button>
                </DialogActions>
            </Dialog>
            {/* edit */}
            <Dialog
                open={openEdit}
                onClose={handleCloseEdit}
                maxWidth="xl" // Tăng kích thước tối đa
                fullWidth // Đảm bảo Dialog mở rộng toàn bộ chiều rộng có thể
                sx={{ '& .MuiDialog-paper': { width: '1000px' } }} // Tăng độ rộng cụ thể
            >
                <DialogTitle>Update Movie</DialogTitle>
                <DialogContent style={{ width: "1000px" }}>
                    <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Name Movie ..."
                                multiline
                                fullWidth
                                name="name"
                                maxRows={10}
                                value={editMovie.name || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Duration ..."
                                multiline
                                fullWidth
                                name="duration"
                                maxRows={10}
                                value={editMovie.duration || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Year of issue ..."
                                multiline
                                fullWidth
                                name="directors"
                                maxRows={10}
                                value={editMovie.directors || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Trailer ..."
                                multiline
                                fullWidth
                                name="trailer"
                                maxRows={10}
                                value={editMovie.trailer || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Cast ..."
                                multiline
                                fullWidth
                                name="cast"
                                maxRows={10}
                                value={editMovie.cast || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid><Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Directors ..."
                                multiline
                                fullWidth
                                name="directors"
                                maxRows={10}
                                value={editMovie.directors || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <Autocomplete
                                multiple
                                id="checkboxes-tags-demo"
                                options={Array.isArray(types) ? types : []} // Đảm bảo luôn là mảng
                                disableCloseOnSelect
                                getOptionLabel={(option) => option.name}
                                value={selectedType} // Lấy từ state `selectedTypes`
                                onChange={(event, newValue) => {
                                    setSelectedType(newValue);
                                    const selectedIds = newValue.map((option) => option.id);
                                    setEditMovie(prev => ({
                                        ...prev,
                                        types: selectedIds, // Cập nhật danh sách ID
                                    }));
                                }}
                                renderOption={(props, option, { selected }) => (
                                    <li {...props} key={option.id}>
                                        <Checkbox
                                            icon={icon}
                                            checkedIcon={checkedIcon}
                                            style={{ marginRight: 8 }}
                                            checked={selected}
                                        />
                                        {option.name}
                                    </li>
                                )}
                                fullWidth
                                name="type"
                                renderInput={(params) => (
                                    <TextField {...params} label="Name Type.." placeholder="Select Types ..." />
                                )}
                            />


                        </Grid>

                    </Grid>


                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseEdit} color="primary">
                        Cancel
                    </Button>
                    <Button color="primary" variant="contained" onClick={handleEditMovie}>
                        Update
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    )
}

export default TableMovie;
