import React, { useEffect, useState } from 'react';
import '../css/navbar.css';
import axios from 'axios'
import ArrowDropDownTwoToneIcon from '@mui/icons-material/ArrowDropDownTwoTone';
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import slugify from "./utils/slugify";

function NavBar() {
  const navigate = useNavigate();
  const [types, setTypes] = useState([]);
  const [nations, setNations] = useState([]);
  const [movies, setMovie] = useState(null)

  useEffect(() => {
    axios.get('http://localhost:8080/website/movieAll')
      .then(response => {
        setMovie(response.data);
      })
      .catch(error => console.error(error));
  }, []);

  useEffect(() => {
    axios.get('http://localhost:8080/website/typeAll')
      .then(response => {
        setTypes(response.data);
      })
      .catch(error => console.error(error));
  }, []);

  useEffect(() => {
    axios.get('http://localhost:8080/website/nationAll')
      .then(response => {
        setNations(response.data);
      })
      .catch(error => console.error(error));
  }, []);

  return (

    <header >
      <div className="header-one">
        <h1>Vivacity Web Agency</h1>
        <p>Creatività e Innovazione per il tuo Business</p>
      </div>
      <div className="header">
        <Link to="/home" className="logo">Trang chủ</Link>
        <nav className="navigation">
          <ul>
            <li><a
              onClick={() =>
                navigate(`/film/${slugify(movies.name)}`, {
                  state: { id: movies.id },
                })}
            >Phim lẻ</a></li>
            <li><a href="/danh-sach/phim-bo">Phim bộ</a></li>
            <li><a href="/danh-sach/phim-chieu-rap">Phim chiếu rạp</a></li>
            <li>
              <a href="#">Thể loại <ArrowDropDownTwoToneIcon className="icon" /></a>
              <ul>
                {types.length > 0 ? (
                  types.map((type, index) => (
                    <li key={index}><a href="#">{type.name}</a></li>
                  ))
                ) : (
                  <li>Loading...</li>
                )}
              </ul>
            </li>

            <li>
              <a href="#">Quốc gia <ArrowDropDownTwoToneIcon className="icon" /></a>
              <ul>
                {nations.length > 0 ? (
                  nations.map((nation, index) => (
                    <li key={index}><a href="#">{nation.name}</a></li>
                  ))
                ) : (
                  <li>Loading...</li>
                )}
              </ul>
            </li>
          </ul>
        </nav>
      </div>

    </header>

  );
}

export default NavBar;
