import React, { useEffect, useState } from 'react';
import axios from 'axios'
import StarTwoToneIcon from '@mui/icons-material/StarTwoTone';
import { useNavigate } from "react-router-dom";
import slugify from "./utils/slugify";


function ListTrending() {
  const [movies, setMovie] = useState(null)
  const [moviesRating, setMovieRating] = useState(null)
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8080/website/listMovieView')
      .then(response => {
        setMovie(response.data);
      })
      .catch(error => console.error(error));
  }, []);
  useEffect(() => {
    axios.get('http://localhost:8080/website/listMovieRating')
      .then(response => {
        setMovieRating(response.data);
      })
      .catch(error => console.error(error));
  }, []);
  return (
    <div className="listrending">
      <h2>Trending</h2>
      <p>- - - - - - - - - - - - - - - - - - - -  </p>
      {Array.isArray(movies) &&
        movies.map((movie, index) => (
          <div className="items" key={index}>
            <div className="item"><img
              src={`data:image/jpeg;base64,${movie.image}`}
              alt={movie.name}
              style={{ width: "50px", height: "50px", objectFit: "cover", borderRadius: "8px" }}
            /></div>
            <div className="item">
              <a onClick={() => {
                                            // Lưu id vào localStorage
                                            localStorage.setItem('movieId', movie.id);
                                            // Điều hướng đến trang chi tiết phim
                                            navigate(`/film/${slugify(moviele.name)}`);
                                        }}>{movie.name}</a>
              <br></br>
              {movie.view} lượt xem
            </div>

          </div>
        ))}
      <br></br>
      <br></br>
      <br></br>
      <h2>Bảng xếp hạng</h2>
      <p>- - - - - - - - - - - - - - - - - - - -  </p>
      {Array.isArray(moviesRating) &&
        moviesRating.map((movierating, index) => (
          <div className="items" key={index}>
            <div className="item"><img
              src={`data:image/jpeg;base64,${movierating.image}`}
              alt={movierating.name}
              style={{ width: "50px", height: "50px", objectFit: "cover", borderRadius: "8px" }}
            /></div>
            <div className="item">
              <a onClick={() =>
                navigate(`/film/${slugify(movierating.name)}`, {
                  state: { id: movierating.id },
                })}>{movierating.name}</a>
              <br></br>
              {movierating.rating} <StarTwoToneIcon sx={{ color: "gold" }} />
            </div>

          </div>
        ))}
    </div>
  );
}

export default ListTrending;
