import { Link, useParams } from "react-router-dom";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import React, { useEffect, useState } from "react";
import axios from 'axios';

function Video() {
    const { slug } = useParams(); // Lấy slug từ URL
    const [movie, setMovie] = useState(null);

    useEffect(() => {
        if (!slug) return;

        // Tìm ID phim từ slug (có thể bạn cần một cách xử lý tìm ID phù hợp từ slug)
        const movieId = slug.split('-').pop(); // Chỉ lấy ID từ slug nếu slug là "name-episodes"

        axios
            .get(`http://localhost:8080/website/movie/${movieId}`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.error(err));
    }, [slug]);

    if (!movie) return <div>Loading...</div>;

    return (
        <div>
            <NavBar />
            <br></br>
            <div className="link">
                <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25, marginLeft: "150px" }} />
                <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name}
            </div>

            <div className="content">
                {/* Hiển thị thông tin chi tiết phim */}
                <h1>{movie.name}</h1>
                <p>{movie.description}</p>
                {/* Các phần còn lại */}
            </div>
        </div>
    );
}

export default Video;
