import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import NavBar from './NavBar';
import Footer from './Footer';

const FilmDetail = () => {
    const { slug } = useParams();
    const [movie, setMovie] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchMovie = async () => {
            try {
                const response = await fetch(`/api/movies/${slug}`); // Đổi nếu API khác
                if (response.ok) {
                    const data = await response.json();
                    setMovie(data);
                } else {
                    console.error("Không tìm thấy phim");
                }
            } catch (error) {
                console.error("Lỗi khi tải phim:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchMovie();
    }, [slug]);

    if (loading) {
        return (
            <div>
                <NavBar />
                <div style={{ textAlign: 'center', marginTop: '100px', marginBottom: '200px', color: '#fff' }}>
                    <h2>Đang tải dữ liệu phim...</h2>
                </div>
            </div>
        );
    }

    if (!movie) {
        return (
            <div>
                <div style={{ textAlign: 'center', marginTop: '100px', marginBottom: '200px', color: 'red' }}>
                    <h2>Không tìm thấy phim</h2>
                </div>
            </div>
        );
    }

    return (
        <div>
            <NavBar />
            <div style={{ color: '#fff', padding: '50px' }}>
                <h1>{movie.name}</h1>
                <img
                    src={`data:image/jpeg;base64,${movie.image}`}
                    alt={movie.name}
                    style={{ width: '300px', borderRadius: '8px' }}
                />
                <p>Thời lượng: {movie.duration} phút</p>
                <p>Phát hành: {movie.namphathanh}</p>
                <p>Thể loại: {movie.style}</p>
                <p>Quốc gia: {movie.nation}</p>
                {/* Thêm các trường khác nếu cần */}
            </div>
            {/* <Footer /> */}
        </div>
    );
};

export default FilmDetail;
