import React, { useEffect, useState } from 'react';
import '../css/navbar.css';
import axios from 'axios'
import ArrowDropDownTwoToneIcon from '@mui/icons-material/ArrowDropDownTwoTone';
import Content from './content';
import Footer from './footer';
function NavBar() {
  const [types, setType] = useState(null)
    const [nations, setNation] = useState(null)

  useEffect(() => {
    axios.get('http://localhost:8080/website/typeAll')
      .then(response => {
        setType(response.data);
      })
      .catch(error => console.error(error));
  }, []);
  useEffect(() => {
    axios.get('http://localhost:8080/website/nationAll')
        .then(response => {
            setNation(response.data);
        })
        .catch(error => console.error(error));
}, []);
  return (

    <header >
      <div className="header-one">
        <h1>Vivacity Web Agency</h1>
        <p>Creatività e Innovazione per il tuo Business</p>
      </div>
      <div className="header">
        <div className="logo">Phim HAY</div>
        <nav className="navigation">
          <ul>
            <li><a href="#">Phim lẻ</a></li>
            <li><a href="#">Phim bộ</a></li>

            <li>
              <a href="#">Thể loại <ArrowDropDownTwoToneIcon className="icon" /></a>
              <ul>
                {types.length > 0 ? (
                  types.map((type, index) => (
                    <li key={index}><a href="#">{type.name}</a></li>
                  ))
                ) : (
                  <li>Loading...</li>
                )}
              </ul>
            </li>

            <li>
              <a href="#">Quốc gia <ArrowDropDownTwoToneIcon className="icon" /></a>
              <ul>
                {nations.length > 0 ? (
                  nations.map((type, index) => (
                    <li key={index}><a href="#">{type.name}</a></li>
                  ))
                ) : (
                  <li>Loading...</li>
                )}
              </ul>
            </li>

          </ul>
        </nav>
      </div>
      <Content />
      <Footer />
    </header>

  );
}

export default NavBar;
