import React, { useEffect, useState } from "react";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import ListTrending from './listrending';
import slugify from "./utils/slugify";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import { Link, useLocation } from "react-router-dom";

function PhimLe() {
  const navigate = useNavigate();
  const location = useLocation();
  const id = location.state?.id;

  const [movies, setMovies] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;

  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:8080/website/movie/styleId/${id}`)
      .then((res) => setMovies(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  // Chia phim bộ và phim khác
  const phimBo = movies.filter(movie => movie.style.name === "Phim bộ");
  const phimKhac = movies.filter(movie => movie.style.name !== "Phim bộ");

  // Phân trang phim bộ
  const totalPages = Math.ceil(phimBo.length / itemsPerPage);
  const paginatedPhimBo = phimBo.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // Gộp phim khác và phim bộ đang phân trang
  const currentMovies = [...phimKhac, ...paginatedPhimBo];

  // Hàm tạo danh sách trang hợp lý
  const renderPagination = () => {
    if (totalPages <= 1) return null;

    const pages = [];

    for (let i = 1; i <= totalPages; i++) {
      if (
        i === 1 ||
        i === totalPages ||
        i === currentPage ||
        i === currentPage - 1 ||
        i === currentPage + 1
      ) {
        pages.push(i);
      } else if (
        (i === currentPage - 2 && currentPage > 3) ||
        (i === currentPage + 2 && currentPage < totalPages - 2)
      ) {
        pages.push("...");
      }
    }

    const uniquePages = [...new Set(pages)];

    return (
      <div className="pagination">
        {uniquePages.map((page, index) =>
          page === "..." ? (
            <span key={index} className="dots">...</span>
          ) : (
            <button
              key={index}
              className={currentPage === page ? "active" : ""}
              onClick={() => setCurrentPage(page)}
            >
              {page}
            </button>
          )
        )}
      </div>
    );
  };

  return (
    <div>
      <NavBar />
      <br />
      <div className="content">
        <div className="link">
          <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25 }} />
          {movies.length > 0 && (
            <div>
              <Link to="/home" className="xemphim">Xem phim</Link> / {movies[0].style.name}
            </div>
          )}
        </div>

        <div className="grid">
          <div className="grid__item grid__item_item4">
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
              {currentMovies.map((movie, index) => (
                <Grid item xs={3} key={index}>
                  <div
                    className="card"
                    onClick={() =>
                      navigate(`/film/${slugify(movie.name)}`, {
                        state: { id: movie.id },
                      })
                    }
                  >
                    <a href="#">
                      <div className="img1">
                        <img
                          src={`data:image/jpeg;base64,${movie.image}`}
                          alt={movie.name}
                          style={{
                            width: "100%",
                            height: "100%",
                            objectFit: "cover",
                            borderRadius: "8px",
                          }}
                        />
                      </div>
                      <div className="title">{movie.name}</div>
                      <div className="text">
                        {movie.style.name === 'Phim bộ'
                          ? `${movie.episodes} Tập Vietsub`
                          : 'Full Vietsub'}
                      </div>
                      <div className="catagory">Phát hành năm: {movie.namphathanh}</div>
                      <div className="icon_video">
                        <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                      </div>
                      {movie.style.name !== 'Phim bộ' && (
                        <div className="views">{movie.duration} phút</div>
                      )}
                    </a>
                  </div>
                </Grid>
              ))}
            </Grid>

            {renderPagination()}
          </div>

          <div className="grid__item grid__item_item5">
            <ListTrending />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PhimLe;
