import React, { useRef, useEffect, useState } from "react";
import ArrowForwardIosRoundedIcon from "@mui/icons-material/ArrowForwardIosRounded";
import ArrowBackIosNewRoundedIcon from "@mui/icons-material/ArrowBackIosNewRounded";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import NavBar from './navbar';
import Footer from './footer';
import Tab from './tab';
import { useParams, useLocation } from "react-router-dom";
import StarTwoToneIcon from '@mui/icons-material/StarTwoTone';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import HomeIcon from '@mui/icons-material/Home';


const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 650,
    height: 450,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};
function Detail() {
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    const carouselRef = useRef(null);

    const willLeftLessThan40pxToScrollEnd = (nextStep) => {
        if (!carouselRef.current) return false;
        const scrollLeftAfterTwoClicks = carouselRef.current.scrollLeft + nextStep * 2;
        return scrollLeftAfterTwoClicks > carouselRef.current.scrollWidth - 40;
    };

    const willLeftLessThan40pxToScrollStart = (nextStep) => {
        if (!carouselRef.current) return false;
        const scrollLeftAfterOneClick = carouselRef.current.scrollLeft - nextStep;
        return scrollLeftAfterOneClick < 40;
    };

    const handleClickGoAhead = () => {
        if (!carouselRef.current) return;
        let nextStep = carouselRef.current.offsetWidth;
        if (willLeftLessThan40pxToScrollEnd(nextStep)) nextStep *= 2;

        carouselRef.current.scroll({
            left: carouselRef.current.scrollLeft + nextStep,
            behavior: "smooth",
        });
    };

    const handleClickGoBack = () => {
        if (!carouselRef.current) return;
        let nextStep = carouselRef.current.offsetWidth;
        if (willLeftLessThan40pxToScrollStart(nextStep)) nextStep *= 2;

        carouselRef.current.scroll({
            left: carouselRef.current.scrollLeft - nextStep,
            behavior: "smooth",
        });
    };

    const [moviestop, setMovieTop] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/website/topphim')
            .then(response => {
                setMovieTop(response.data);
            })
            .catch(error => console.error(error));
    }, []);

    const { id } = useParams(); // Lấy ID từ URL
    const [movie, setMovie] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8080/website/movie/${id}`)
            .then(response => setMovie(response.data))
            .catch(error => console.error(error));
    }, [id]);

    if (!movie) return <p>Đang tải dữ liệu...</p>;
    return (

        <div>
            <NavBar />
            <HomeIcon color="primary" sx={{ color: "#8B4513" }}/>
            <div className="content">
                <div className="grid">
                    <div className="grid__item grid__item_item4">
                        <Grid container rowSpacing={0} columnSpacing={{ xs: 1, sm: 2, md: 0 }}>
                            <Grid size={6}>
                                <div className="card" style={{ width: '300px', height: '400px' }}>
                                    <a href="#">
                                        <div className="img1">
                                            <img
                                                src={`data:image/jpeg;base64,${movie.image}`}
                                                alt={movie.name}
                                                style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }}
                                            />
                                        </div>

                                        {/* <div className="title">tên</div> */}
                                        <div className="icon_video_detail"><PlayCircleRoundedIcon sx={{ width: 90, height: 90 }} /></div>
                                        {/* <div className="icon_video"><PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} /></div> */}
                                    </a>
                                </div>
                                <button onClick={handleOpen} className="button_trailer" >Trailer</button>
                            </Grid>
                            <Grid size={6}>
                                <div className="card_infor">
                                    <h2>{movie.name}</h2>
                                    <hr style={{ width: "520px" }}></hr>
                                    <br></br>
                                    <div>
                                        <span className="line-break">Trạng thái : Full Vietsub</span>
                                        <span className="line-break">Thời lượng : {movie.duration} phút</span>
                                        <span className="line-break">Số tập : {movie.episodes} tập</span>
                                        <span className="line-break">
                                            Tình trạng : {
                                                movie.status === "0" ? "Đang chiếu" :
                                                    movie.status === "1" ? "Chưa chiếu" :
                                                        "Hoàn thành"
                                            }
                                        </span>
                                        <span className="line-break">Ngôn ngữ : Vietsub</span>
                                        <span className="line-break">Năm sản xuất : {movie.namphathanh}</span>
                                        <span className="line-break">Quốc gia : {movie.nation.name}</span>
                                        <span className="line-break">Đạo diễn : {movie.directors}</span>
                                        <span className="line-break">Diễn viên: {movie.cast}</span>
                                        <span className="line-break">
                                            Thể loại: {movie.types.map(type => type.name).join(", ")}
                                        </span>
                                        <span className="line-break">Đánh giá : {movie.rating} <StarTwoToneIcon sx={{ color: "gold" }} /></span>
                                    </div>
                                </div>
                            </Grid>
                        </Grid>
                        <br></br>
                        <div className="infor_flim">
                            <h1>Nội dung phim</h1>
                            <hr></hr>
                            <br></br>
                            {movie.description}
                        </div>
                        <br></br>

                        <div className="comment__container">
                            <b className="comment__title">0 comment</b>
                            <hr></hr>
                            <br></br>
                            <div className="comment__body">
                                <img src="https://img.ophim.live/uploads/movies/yeu-la-mu-quang-brazil-phan-3-thumb.jpg"
                                    style={{ width: "50px", height: "50px" }}
                                    className="comment__avatar" alt="avatar" />
                                <div>
                                    <textarea className="comment__textarea" placeholder="Bình luận ..." rows="8"></textarea>
                                    <div className="comment__post">
                                        <div className="comment__info"></div>
                                        <div>
                                            <button className="comment__send">Post Comment</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <br></br>
                        <p>Phim đề cử</p>
                        <br></br>
                        <div id="wrapper">
                            <div id="carousel" ref={carouselRef}>
                                {Array.isArray(moviestop) &&
                                    moviestop.map((movietop, index) => (
                                        <div className="container_card" key={index}>
                                            <div className="img-container">
                                                <span className="vietsub">{movietop.episodes} Tập Vietsub</span>
                                                <span className="trending">Trending</span>

                                                <img
                                                    src={`data:image/jpeg;base64,${movietop.image}`}
                                                    alt={movietop.name}
                                                    style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }}
                                                />
                                            </div>
                                            <ul className="social-media">
                                                <li>
                                                    <a href="#"><PlayCircleRoundedIcon sx={{ color: "#ffffff", width: 50, height: 50 }} />
                                                    </a>
                                                </li>
                                            </ul>
                                            <div className="user-info">
                                                <h3>{movietop.name}</h3>
                                                <span>Năm phát hành: {movietop.namphathanh}</span>
                                            </div>
                                        </div>
                                    ))}
                            </div>
                        </div>
                        <div className="buttons">
                            <button onClick={handleClickGoBack} >
                                <ArrowBackIosNewRoundedIcon />
                            </button>
                            <button onClick={handleClickGoAhead}>
                                <ArrowForwardIosRoundedIcon />
                            </button>
                        </div>
                    </div>
                    <div className="grid__item grid__item_item5">
                        <Tab />
                    </div>
                </div>
            </div>
            <Footer />
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"

            >
                <Box sx={style}>
                    <Typography id="modal-modal-title" variant="h6" component="h2">
                        Trailer
                    </Typography>
                    <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                        <iframe
                            src={movie.trailer}
                            title="YouTube video player" frameBorder="0"
                            width="100%"
                            height="350px"
                            allow="accelerometer; autoplay; clipboard-write;
                          encrypted-media; gyroscope; picture-in-picture; 
                          web-share" referrerPolicy="strict-origin-when-cross-origin" allowFullScreen></iframe>
                    </Typography>
                </Box>
            </Modal>
        </div>


    );
}

export default Detail;
