import { Link } from "react-router-dom";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import React, { useEffect, useState } from "react";
import axios from 'axios';
import { useParams } from "react-router-dom";

function Video() {
    const { id } = useParams(); // Lấy ID từ URL
    const [movie, setMovie] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8080/website/movie/${id}`)
            .then(response => setMovie(response.data))
            .catch(error => console.error(error));
    }, [id]);

    if (!movie) return <p>Đang tải dữ liệu...</p>;
    return (
        <div>
            <NavBar />
            <br></br>
            <div className="link">
                <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25, marginLeft: "150px" }} />
                <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name}
            </div>

            <div className="content">

            </div>
        </div>
    );
}

export default Video;
