import React, { useRef, useEffect, useState } from "react";
import ArrowForwardIosRoundedIcon from "@mui/icons-material/ArrowForwardIosRounded";
import ArrowBackIosNewRoundedIcon from "@mui/icons-material/ArrowBackIosNewRounded";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import NavBar from './navbar';
import Footer from './footer';
import Tab from './tab';
import { useParams, useLocation } from "react-router-dom";
import StarTwoToneIcon from '@mui/icons-material/StarTwoTone';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import HomeIcon from '@mui/icons-material/Home';
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import slugify from "./utils/slugify";
import MovieTrending from './movietrending';


const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 650,
    height: 450,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};
function Detail() {
    const navigate = useNavigate();
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);



    // const { slug } = useParams();
    const location = useLocation();
    const id = location.state?.id; // üëà Nh·∫≠n l·∫°i id t·ª´ state

    const [movie, setMovie] = useState(null);

    useEffect(() => {
        if (!id) return;

        axios
            .get(`http://localhost:8080/website/movie/${id}`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.error(err));
    }, [id]);


    if (!movie) return <div>Loading...</div>;

    const handleClick = () => {
        if (movie.style === 'Phim l·∫ª' || movie.style === 'Phim chi·∫øu r·∫°p') {
            const episodeSlug = (String(movie.episodes) === '1') ? 'tap-full' : slugify(movie.episodes);

            // T·∫°o URL t·ª´ movie.name v√† episodeSlug v·ªõi d·∫•u "/"
            const slug = `${slugify(movie.name)}/${episodeSlug}`; // S·ª≠ d·ª•ng "/" ƒë·ªÉ ph√¢n t√°ch
            // ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang Video, truy·ªÅn state g·ªìm id c·ªßa phim
            navigate(`/film/${slug}`, { state: { id: movie.id } });
        } else {
            const episodeSlug = (String(movie.episodes) === '1') ? 'tap-full' : slugify(movie.episodes);

            // T·∫°o URL t·ª´ movie.name v√† episodeSlug v·ªõi d·∫•u "/"
            const slug = `${slugify(movie.name)}-${episodeSlug}`; // S·ª≠ d·ª•ng "/" ƒë·ªÉ ph√¢n t√°ch
            // ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang Video, truy·ªÅn state g·ªìm id c·ªßa phim
            navigate(`/film/${slug}`, { state: { id: movie.id } });
        }

        // Ki·ªÉm tra n·∫øu episodes l√† "1" th√¨ thay th·∫ø th√†nh "tap-full"


        // ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang Video, truy·ªÅn state g·ªìm id c·ªßa phim
        // navigate(`/film/${slug}`, { state: { id: movie.id } });
    };


    return (

        <div>
            <NavBar />
