import React, { useEffect, useState } from "react";
import { useLocation, Link } from "react-router-dom";
import axios from "axios";
import NavBar from "./NavBar"; // Giả sử bạn đã có component NavBar
import HomeIcon from "@mui/icons-material/Home";
import slugify from "slugify"; // Đảm bảo bạn đã cài slugify

function Video() {
    const location = useLocation();
    const id = location.state?.id;

    const [movie, setMovie] = useState(null);

    useEffect(() => {
        if (!id) return;

        axios
            .get(`http://localhost:8080/website/movie/${id}`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.error(err));
    }, [id]);

    if (!movie) return <div>Loading...</div>;

    return (
        <div>
            <NavBar />
            <br />
            <div className="link">
                <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25, marginLeft: "150px" }} />
                <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style?.name} / {movie.name}
            </div>

            <div className="content">
                {/* Hiển thị nội dung phim ở đây */}
                <h2>{movie.name}</h2>
                <p>Thể loại: {movie.style?.name}</p>
                <p>Số tập: {movie.episodes}</p>
                <video width="800" controls>
                    <source src={movie.videoUrl} type="video/mp4" />
                    Trình duyệt của bạn không hỗ trợ video.
                </video>
            </div>
        </div>
    );
}

export default Video;
