import React, { useRef, useEffect, useState } from "react";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import ListTrending from './listrending';
import slugify from "./utils/slugify";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import { Link } from "react-router-dom";
import { useParams, useLocation } from "react-router-dom";

function PhimLe() {
  // const [moviesle, setMovieLe] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();
  const id = location.state?.id; //  Nhận lại id từ state
  const [movies, setMovie] = useState(null);

  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:8080/website/movie/styleId/${id}`)
      .then((res) => setMovie(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentMovies = movies?.slice(startIndex, endIndex);
  const totalPages = movies ? Math.ceil(movies.length / itemsPerPage) : 0;
  
  
  return (
    <div>
      <NavBar />
      <br></br>
      <div className="content">
        <div className="link">
          <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25 }} />
          {Array.isArray(movies) && movies.length > 0 && (
            <div>
              <Link to="/home" className="xemphim">Xem phim</Link> / {movies[0].style.name}
            </div>
          )}


        </div>
        <div className="grid">
          <div className="grid__item grid__item_item4">
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>

              {Array.isArray(currentMovies) &&
                currentMovies.map((movie, index) => (
                  <Grid item xs={3} key={index}>
                    {(movie.style.name === 'Phim lẻ' || movie.style.name === 'Phim chiếu rạp') && (
                      <>
                        <div className="card"
                          onClick={() =>
                            navigate(`/film/${slugify(movie.name)}`, {
                              state: { id: movie.id },
                            })
                          }
                        >
                          <a href="">
                            <div className="img1">
                              <img
                                src={`data:image/jpeg;base64,${movie.image}`}
                                alt={movie.name}
                                style={{
                                  width: "100%",
                                  height: "100%",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                }}
                              />
                            </div>
                            <div className="title">{movie.name}</div>
                            <div className="text">Full Vietsub</div>
                            <div className="catagory">Phát hành năm: {movie.namphathanh} </div>
                            <div className="icon_video">
                              <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                            </div>
                            <div className="views">{movie.duration} phút</div>
                          </a>
                        </div>
                      </>
                    )}
                    {movie.style.name === 'Phim bộ' && (
                      <>
                        <div className="card" onClick={() =>
                          navigate(`/film/${slugify(movie.name)}`, {
                            state: { id: movie.id },
                          })
                        }>
                          <a href="">
                            <div className="img1">
                              <img
                                src={`data:image/jpeg;base64,${movie.image}`}
                                alt={movie.name}
                                style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }}
                              />
                            </div>
                            <div className="title">{movie.name}</div>
                            <div className="text">{movie.episodes} Tập Vietsub</div>
                            <div className="catagory">Phát hành năm: {movie.namphathanh} </div>
                            <div className="icon_video">
                              <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                            </div>
                          </a>
                        </div>

                      </>
                    )}
                  </Grid>
                ))}
            </Grid>
          </div>
          <div className="grid__item grid__item_item5">   <ListTrending /></div>
        </div>
      </div>
    </div>
  );
}

export default PhimLe;
