import React, { useEffect, useState } from "react";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import ListTrending from './listrending';
import slugify from "./utils/slugify";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import { Link, useLocation } from "react-router-dom";
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';

function PhimLe() {
  const navigate = useNavigate();
  const location = useLocation();
  const id = location.state?.id;
  const [movies, setMovie] = useState(null);
  const [value, setValue] = React.useState(null);
  const [inputValue, setInputValue] = React.useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 20;
  const [types, setType] = useState(null)

  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:8080/website/movie/styleId/${id}`)
      .then((res) => setMovie(res.data))
      .catch((err) => console.error(err));
  }, [id]);
  useEffect(() => {
    axios.get('http://localhost:8080/website/typeAll')
      .then(response => {
        setType(response.data);
      })
      .catch(error => console.error(error));
  }, []);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentMovies = movies?.slice(startIndex, endIndex);
  const totalPages = movies ? Math.ceil(movies.length / itemsPerPage) : 0;

  return (
    <div>
      <NavBar />
      <br />
      <div className="content">
        <div className="link">
          <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25 }} />
          {Array.isArray(movies) && movies.length > 0 && (
            <div>
              <Link to="/home" className="xemphim">Xem phim</Link> / {movies[0].style.name}
            </div>
          )}
        </div>
        <br></br>
        <div className="shearch">
        <Autocomplete
  options={type}
  getOptionLabel={(option) => option.name || ''} // Nếu dữ liệu có field là "name"
  value={value}
  onChange={(event, newValue) => {
    setValue(newValue);
  }}
  inputValue={inputValue}
  onInputChange={(event, newInputValue) => {
    setInputValue(newInputValue);
  }}
  id="controllable-states-demo"
  sx={{ width: 300 }}
  renderInput={(params) => (
    <TextField
      {...params}
      label="Thể loại"
      InputProps={{
        ...params.InputProps,
        sx: {
          color: '#e6e6e6',
          backgroundColor: '#2a2a2a',
          '& .MuiOutlinedInput-notchedOutline': {
            borderColor: '#000',
          },
          '&:hover .MuiOutlinedInput-notchedOutline': {
            borderColor: '#000',
          },
          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
            borderColor: '#000',
          },
        }
      }}
      InputLabelProps={{
        sx: { color: '#e6e6e6' }
      }}
    />
  )}
/>



        </div>
        <div className="grid">
          <div className="grid__item grid__item_item4">
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
              {Array.isArray(currentMovies) &&
                currentMovies.map((movie, index) => (
                  <Grid item xs={3} key={index}>
                    <div
                      className="card"
                      onClick={() =>
                        navigate(`/film/${slugify(movie.name)}`, {
                          state: { id: movie.id },
                        })
                      }
                    >
                      <a href="#">
                        <div className="img1">
                          <img
                            src={`data:image/jpeg;base64,${movie.image}`}
                            alt={movie.name}
                            style={{
                              width: "100%",
                              height: "100%",
                              objectFit: "cover",
                              borderRadius: "8px",
                            }}
                          />
                        </div>
                        <div className="title">{movie.name}</div>
                        {movie.style.name === 'Phim bộ' ? (
                          <div className="text">{movie.episodes} Tập Vietsub</div>
                        ) : (
                          <div className="text">Full Vietsub</div>
                        )}
                        <div className="catagory">Phát hành năm: {movie.namphathanh}</div>
                        <div className="icon_video">
                          <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                        </div>
                        {movie.style.name !== 'Phim bộ' && (
                          <div className="views">{movie.duration} phút</div>
                        )}
                      </a>
                    </div>
                  </Grid>
                ))}
            </Grid>

            {/* PHÂN TRANG */}
            {totalPages > 1 && (
              <div className="pagination">
                {Array.from({ length: totalPages }, (_, i) => i + 1)
                  .filter((page) =>
                    page === 1 ||
                    page === totalPages ||
                    Math.abs(page - currentPage) <= 1
                  )
                  .reduce((acc, page, index, array) => {
                    if (index > 0 && page - array[index - 1] > 1) {
                      acc.push('...');
                    }
                    acc.push(page);
                    return acc;
                  }, [])
                  .map((item, index) =>
                    item === '...' ? (
                      <span key={`ellipsis-${index}`} className="ellipsis">...</span>
                    ) : (
                      <button
                        key={item}
                        onClick={() => setCurrentPage(item)}
                        className={item === currentPage ? 'active' : ''}
                      >
                        {item}
                      </button>
                    )
                  )}
              </div>
            )}

          </div>
          <div className="grid__item grid__item_item5">
            <ListTrending />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PhimLe;
