import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function Detail() {
    const { slug } = useParams();
    const navigate = useNavigate();
    const [id, setId] = useState(null);

    useEffect(() => {
        const storedId = localStorage.getItem('currentMovieId');
        if (storedId) {
            setId(storedId);
        } else {
            // Không có id -> có thể user vào thẳng bằng URL -> chuyển hướng về trang chủ hoặc hiển thị lỗi
            console.warn('No movie ID found!');
            navigate('/');
        }
    }, [navigate]);

    return (
        <div>
            <h1>Chi tiết phim: {slug}</h1>
            <p>Movie ID: {id}</p>
            {/* Từ ID này, bạn fetch thông tin phim từ server */}
        </div>
    );
}

export default Detail;
