import React, { useRef, useEffect, useState } from "react";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import ListTrending from './listrending';
import slugify from "./utils/slugify";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import { Link } from "react-router-dom";

function PhimLe() {
  const [moviesle, setMovieLe] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8080/website/movieLe')
      .then(response => {
        setMovieLe(response.data);
      })
      .catch(error => console.error(error));
  }, []);


  // Generate visible page numbers for pagination controls
  const getVisiblePageNumbers = () => {
    const pages = [];

    if (totalPages <= 10) {
      for (let i = 1; i <= totalPages; i++) pages.push(i);
    } else {
      const left = Math.max(currentPage - 1, 4);
      const right = Math.min(currentPage + 1, totalPages - 3);

      // Always show first 3 pages
      pages.push(1, 2, 3);

      if (left > 4) pages.push('...');

      // Pages around current
      for (let i = left; i <= right; i++) {
        pages.push(i);
      }

      if (right < totalPages - 3) pages.push('...');

      // Always show last 3 pages
      pages.push(totalPages - 2, totalPages - 1, totalPages);
    }

    return pages;
  };

  const visiblePageNumbers = getVisiblePageNumbers();
  return (
    <div>
      <NavBar />
      <br></br>
      <div className="content">
        <div className="link">
          <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25 }} />
          <Link to="/home" className="xemphim">Xem phim</Link> / Phim lẻ
        </div>
        <div className="grid">
          <div className="grid__item grid__item_item4">
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
              {Array.isArray(currentMovies) &&
                currentMovies.map((moviele, index) => (
                  <Grid item xs={3} key={index}>
                    <div
                      className="card"
                      onClick={() =>
                        navigate(`/film/${slugify(moviele.name)}`, {
                          state: { id: moviele.id },
                        })
                      }
                    >
                      <a href="#">
                        <div className="img1">
                          <img
                            src={`data:image/jpeg;base64,${moviele.image}`}
                            alt={moviele.name}
                            style={{
                              width: '100%',
                              height: '100%',
                              objectFit: 'cover',
                              borderRadius: '8px',
                            }}
                          />
                        </div>
                        <div className="title">{moviele.name}</div>
                        <div className="text">Full Vietsub</div>
                        <div className="catagory">
                          Phát hành năm: {moviele.namphathanh}
                        </div>
                        <div className="icon_video">
                          <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                        </div>
                        <div className="views">{moviele.duration} phút</div>
                      </a>
                    </div>
                  </Grid>
                ))}
            </Grid>
            <br></br>
            <div className="pagination">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Previous
              </button>

              {visiblePageNumbers.map((number, idx) =>
                number === '...' ? (
                  <span key={idx} className="dots">
                    ...
                  </span>
                ) : (
                  <button
                    key={number}
                    onClick={() => setCurrentPage(number)}
                    className={currentPage === number ? 'active' : ''}
                  >
                    {number}
                  </button>
                )
              )}

              <button
                onClick={() =>
                  setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                }
                disabled={currentPage === totalPages}
              >
                Next
              </button>
            </div>
          </div>
          <div className="grid__item grid__item_item5">   <ListTrending /></div>
        </div>
      </div>
    </div>
  );
}

export default PhimLe;
