import React, { useEffect, useState } from 'react';
import axios from 'axios'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { Paper, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit'
import TextField from '@mui/material/TextField';
import TablePagination from '@mui/material/TablePagination';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Autocomplete from '@mui/material/Autocomplete';
import { TextareaAutosize as BaseTextareaAutosize } from '@mui/base/TextareaAutosize';
import { styled } from '@mui/system';
import Checkbox from '@mui/material/Checkbox';
import CheckBoxOutlineBlankIcon from '@mui/icons-material/CheckBoxOutlineBlank';
import CheckBoxIcon from '@mui/icons-material/CheckBox';

const icon = <CheckBoxOutlineBlankIcon fontSize="small" />;
const checkedIcon = <CheckBoxIcon fontSize="small" />;


function Movie() {
    const [movies, setMovie] = useState(null)
    const [deleteId, setDeleteId] = useState(null)
    const [confirmOpen, setConfirmOpen] = useState(false)
    const [openEdit, setOpenEdit] = useState(false)
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [image, setImage] = useState(null);
    const [types, setType] = useState(null)
    const [selectedType, setSelectedType] = useState(null);
    const [styles, setStyle] = useState(null)
    const [selectedStyle, setSelectedStyle] = useState(null);
    const [nations, setNation] = useState(null)
    const [selectedNation, setSelectedNation] = useState(null);
    // const [snackbarOpen, setSnackbarOpen] = useState(false)
    const handleImageChange = async (event) => {
        const file = event.target.files[0];
        if (!file) return;
    
        const formData = new FormData();
        formData.append("file", file);
    
        try {
            const response = await fetch("http://localhost:8080/api/movie/upload-image", {
                method: "POST",
                body: formData,
            });
    
            if (response.ok) {
                const imageUrl = await response.text();
                setNewMovie((prev) => ({ ...prev, image: imageUrl }));
            } else {
                console.error("Failed to upload image");
            }
        } catch (error) {
            console.error("Error uploading image:", error);
        }
    };
    
    const handleRemoveImage = () => {
        setImage(null);
        document.getElementById("file-input").value = "";
    };
    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };
    const [newMovie, setNewMovie] = useState({})
    const [editMovie, setEditMovie] = useState({})
    const handleClickOpenEdit = (movie) => {
        setEditMovie(movie)
        setOpenEdit(true)
    }

    const handleChange = (event) => {
        const { name, value } = event.target; // Lấy name và value từ sự kiện onChange
        setNewMovie((prev) => ({
            ...prev,
            [name]: value, // Cập nhật giá trị của thuộc tính tương ứng
        }));
    };

    const handleConfirmOpen = (id) => {
        setDeleteId(id)
        setConfirmOpen(true)
    }
    const handleConfirmClose = (id) => {
        setDeleteId(null)
        setConfirmOpen(false)
    }
    const handleCloseEdit = () => {
        setOpenEdit(false)
    }

    const handleChangeEdit = (event) => {
        setEditMovie({
            ...editMovie,
            [event.target.name]: event.target.value
        });
    };


    // delete
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/website/movie/${id}`);
            setMovie(movies.filter(movie => movie.id !== id));
            handleConfirmClose()
        } catch (error) {
            alert('There was an error deleting the style')
        }
    };
    // add
    const handleAdd = async () => {
        try {
            if (!newMovie.name.trim()) {
                window.alert("Vui lòng nhập tên name!");
                return;
            }
            // Kiểm tra xem tên đã tồn tại trong danh sách chưa
            const isDuplicate = movies.some(movie => movie.name === newMovie.name.trim());
            if (isDuplicate) {
                window.alert("Type này đã tồn tại!");
                return;
            }

            const response = await axios.post('http://localhost:8080/website/movie', newMovie);

            setMovie([...movies, response.data]);
            setNewMovie({ name: '' });

        } catch (error) {
            console.error("Lỗi khi thêm loại:", error);
        }
    };

    // Update
    const handleEditMovie = async () => {
        try {

            const response = await axios.put('http://localhost:8080/website/movie/${editMovie.id}', editMovie);

            setMovie(prevMovies =>
                prevMovies.map(movie =>
                    movie.id === editMovie.id ? response.data : movie
                )
            );

            handleCloseEdit();
        } catch (error) {
            console.error('Lỗi xảy ra khi cập nhật:', error);
        }
    };
    useEffect(() => {
        axios.get('http://localhost:8080/website/movieAll')
            .then(response => {
                setMovie(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    useEffect(() => {
        axios.get('http://localhost:8080/website/typeAll')
            .then(response => {
                setType(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    useEffect(() => {
        axios.get('http://localhost:8080/website/styleAll')
            .then(response => {
                setStyle(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    useEffect(() => {
        axios.get('http://localhost:8080/website/nationAll')
            .then(response => {
                setNation(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    return (
        <Box
            component="form"
            sx={{ '& > :not(movie)': { maxWidth: '100%' } }}
            noValidate
            autoComplete="off"
        >
            <section className="page-content">
                <h1>Movie</h1>
                <hr />
                <br />
                <div className="container">
                    <div className="row">
                        <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Name Nation ..."
                                    multiline
                                    fullWidth
                                    value={newMovie.name}
                                    maxRows={10}
                                    name="name" // Đảm bảo name trùng với key trong state                                  
                                    onChange={handleChange}
                                />

                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Duration ..."
                                    multiline
                                    fullWidth
                                    name="duration"
                                    maxRows={10}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Episodes ..."
                                    multiline
                                    fullWidth
                                    name="episodes"
                                    maxRows={10}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Year of issue ..."
                                    multiline
                                    fullWidth
                                    name="namphathanh"
                                    maxRows={10}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Trailer ..."
                                    multiline
                                    fullWidth
                                    name="trailer"
                                    maxRows={10}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Cast ..."
                                    multiline
                                    fullWidth
                                    name='cast'
                                    maxRows={10}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Directors ..."
                                    multiline
                                    fullWidth
                                    name='directors'
                                    maxRows={10}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <Autocomplete
                                    multiple
                                    id="checkboxes-tags-demo"
                                    options={types}
                                    disableCloseOnSelect
                                    getOptionLabel={(option) => option.name}
                                    onChange={(event, newValue) => {
                                        console.log("Selected Types:", newValue);
                                        setNewMovie((prev) => ({
                                            ...prev,
                                            types: newValue.map((type) => ({ id: type.id })), // Lưu danh sách ID của types
                                        }));
                                    }}
                                    renderOption={(props, option, { selected }) => {
                                        const { key, ...optionProps } = props;
                                        return (
                                            <li key={key} {...optionProps}>
                                                <Checkbox
                                                    icon={icon}
                                                    checkedIcon={checkedIcon}
                                                    style={{ marginRight: 8 }}
                                                    checked={selected}
                                                />
                                                {option.name}
                                            </li>
                                        );
                                    }}
                                    fullWidth
                                    name='types'
                                    renderInput={(params) => (
                                        <TextField {...params} label="Checkboxes" placeholder="Select Types ..." />
                                    )}
                                />
                            </Grid>

                            <Grid item xs={6}>
                                <Autocomplete
                                    disablePortal
                                    options={styles} // Truyền danh sách đối tượng đầy đủ
                                    getOptionLabel={(option) => option.name} // Hiển thị name trong dropdown
                                    onChange={(event, newValue) => {
                                        console.log("Selected Style:", newValue);
                                        setNewMovie((prev) => ({
                                            ...prev,
                                            style: newValue ? { id: newValue.id } : null, // Lưu ID vào newMovie
                                        }));
                                    }}
                                    name='style'
                                    fullWidth
                                    renderInput={(params) => <TextField {...params} label="Name Style ..." />}
                                />
                            </Grid>


                            <Grid item xs={6}>
                                <Autocomplete
                                    disablePortal
                                    options={nations}
                                    getOptionLabel={(option) => option.name}
                                    onChange={(event, newValue) => {

                                        setNewMovie((prev) => ({
                                            ...prev,
                                            nation: newValue ? { id: newValue.id } : null, // Cập nhật ID vào newMovie
                                        }));
                                    }}
                                    name="nation"
                                    fullWidth
                                    renderInput={(params) => (
                                        <TextField {...params} label="Name Nation ..." />
                                    )}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Link Film ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    name='link'
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <Autocomplete
                                    disablePortal
                                    options={[
                                        { label: "Đang chiếu", value: "0" },
                                        { label: "Chưa chiếu", value: "1" },
                                    ]}
                                    fullWidth
                                    name="status"
                                    value={
                                        newMovie.status
                                            ? { label: newMovie.status === "0" ? "Đang chiếu" : "Chưa chiếu", value: newMovie.status }
                                            : null
                                    }
                                    onChange={(event, newValue) => {
                                        setNewMovie((prev) => ({ ...prev, status: newValue ? newValue.value : "" }));
                                    }}
                                    getOptionLabel={(option) => option.label}
                                    renderInput={(params) => (
                                        <TextField {...params} label="Trạng thái ..." />
                                    )}
                                />
                            </Grid>


                            <Grid item xs={6}>
                                <TextareaAutosize
                                    name="description"
                                    onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })}
                                    aria-label="empty textarea"
                                    placeholder="Description ..."
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <div className="mb-1">
                                    Image <span className="font-css top">*</span>
                                    <div>
                                        <input type="file" name='image' id="file-input" onChange={handleImageChange} />
                                    </div>
                                    <br></br>
                                    {image && (
                                        <div className="mt-2">
                                            <img style={{ width: '100px' }} src={image} alt="Selected" />
                                            <button onClick={handleRemoveImage} color="primary" >
                                                Delete image
                                            </button>
                                        </div>
                                    )}
                                </div>
                            </Grid>
                        </Grid>

                    </div>
                    <br></br>
                    <div className="row">
                        <Button onClick={handleAdd} color="primary" variant="contained">
                            Add
                        </Button>
                    </div>
                    <br></br>
                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow style={{ backgroundColor: 'Gray' }}>
                                    <TableCell align="center">STT</TableCell>
                                    <TableCell align="center">Name Style</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {Array.isArray(movies) && movies.length > 0 ? (
                                    movies.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((movie, index) => (
                                        <TableRow key={movie.id}>
                                            <TableCell align="center">{page * rowsPerPage + index + 1}</TableCell>
                                            <TableCell align="center">{movie.name}</TableCell>
                                            <TableCell sx={{ display: 'flex', justifyContent: 'center' }}>
                                                {/* <IconButton color="primary" onClick={() => handleConfirmOpen(movie.id)}>
                                                    <DeleteIcon />
                                                </IconButton> */}
                                                <IconButton color="primary" onClick={() => handleClickOpenEdit(movie)}>
                                                    <EditIcon />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                ) : (
                                    <TableRow>
                                        <TableCell colSpan={3} align="center">Loading...</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 10, 25]}
                            component="div"
                            count={movies != null ? movies.length : 0}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </TableContainer>


                    {/* Xoa */}
                    <Dialog open={confirmOpen} onClose={handleConfirmClose}>
                        <DialogTitle>Confirm DeleteId</DialogTitle>
                        <DialogContent>Are you sure you want to delete this Style?</DialogContent>
                        <DialogActions>
                            <Button onClick={handleConfirmClose} color='primary'>
                                Cancel
                            </Button>
                            <Button onClick={() => { handleDelete(deleteId); }} color='secondary' variant='contained'>
                                Delete
                            </Button>
                        </DialogActions>
                    </Dialog>
                    {/* edit */}
                    <Dialog open={openEdit} onClose={handleCloseEdit}>
                        <DialogTitle>Update Movie</DialogTitle>
                        <DialogContent>
                            <TextField
                                name="name"
                                type="text"
                                value={editMovie.name || ''}
                                onChange={handleChangeEdit}
                            />
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleCloseEdit} color="primary">
                                Cancel
                            </Button>
                            <Button color="primary" variant="contained" onClick={handleEditMovie}>
                                Update
                            </Button>
                        </DialogActions>
                    </Dialog>
                </div>

            </section>
        </Box>
    )
}
const blue = {
    100: '#DAECFF',
    200: '#b6daff',
    400: '#3399FF',
    500: '#007FFF',
    600: '#0072E5',
    900: '#003A75',
};

const grey = {
    50: '#F3F6F9',
    100: '#E5EAF2',
    200: '#DAE2ED',
    300: '#C7D0DD',
    400: '#B0B8C4',
    500: '#9DA8B7',
    600: '#6B7A90',
    700: '#434D5B',
    800: '#303740',
    900: '#1C2025',
};

const TextareaAutosize = styled(BaseTextareaAutosize)(
    ({ theme }) => `
    box-sizing: border-box;
    width: 590px;
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 0.875rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 8px 12px;
    border-radius: 8px;
    color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
    background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
    border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
    box-shadow: 0 2px 2px ${theme.palette.mode === 'dark' ? grey[900] : grey[50]};
  
    &:hover {
      border-color: ${blue[400]};
    }
  
    &:focus {
      border-color: ${blue[400]};
      box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[600] : blue[200]};
    }
  
    /* firefox */
    &:focus-visible {
      outline: 0;
    }
  `,
);
export default Movie;
