import React, { useRef, useEffect, useState } from "react";
import ArrowForwardIosRoundedIcon from "@mui/icons-material/ArrowForwardIosRounded";
import ArrowBackIosNewRoundedIcon from "@mui/icons-material/ArrowBackIosNewRounded";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';


function Content() {
    const carouselRef = useRef(null);

    const willLeftLessThan40pxToScrollEnd = (nextStep) => {
        if (!carouselRef.current) return false;
        const scrollLeftAfterTwoClicks = carouselRef.current.scrollLeft + nextStep * 2;
        return scrollLeftAfterTwoClicks > carouselRef.current.scrollWidth - 40;
    };

    const willLeftLessThan40pxToScrollStart = (nextStep) => {
        if (!carouselRef.current) return false;
        const scrollLeftAfterOneClick = carouselRef.current.scrollLeft - nextStep;
        return scrollLeftAfterOneClick < 40;
    };

    const handleClickGoAhead = () => {
        if (!carouselRef.current) return;
        let nextStep = carouselRef.current.offsetWidth;
        if (willLeftLessThan40pxToScrollEnd(nextStep)) nextStep *= 2;

        carouselRef.current.scroll({
            left: carouselRef.current.scrollLeft + nextStep,
            behavior: "smooth",
        });
    };

    const handleClickGoBack = () => {
        if (!carouselRef.current) return;
        let nextStep = carouselRef.current.offsetWidth;
        if (willLeftLessThan40pxToScrollStart(nextStep)) nextStep *= 2;

        carouselRef.current.scroll({
            left: carouselRef.current.scrollLeft - nextStep,
            behavior: "smooth",
        });
    };

    const [moviesle, setMovieLe] = useState([]);
    const [moviesbo, setMovieBo] = useState([]);
    const [moviestop, setMovieTop] = useState([]);
    const [moviesChieuRap, setMovieChieuRap] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/website/movieLe')
            .then(response => {
                // Chỉ lấy 12 dữ liệu mới nhất
                setMovieLe(response.data.sort((a, b) => new Date(b.releaseDate) - new Date(a.releaseDate)).slice(0, 12));

            })
            .catch(error => console.error(error));
    }, []);

    useEffect(() => {
        axios.get('http://localhost:8080/website/movieBo')
            .then(response => {
                // Chỉ lấy 12 dữ liệu mới nhất
                setMovieBo(response.data.sort((a, b) => new Date(b.releaseDate) - new Date(a.releaseDate)).slice(0, 12));

            })
            .catch(error => console.error(error));
    }, []);

    useEffect(() => {
        axios.get('http://localhost:8080/website/movieChieuRap')
            .then(response => {
                // Chỉ lấy 12 dữ liệu mới nhất
                setMovieChieuRap(response.data.sort((a, b) => new Date(b.releaseDate) - new Date(a.releaseDate)).slice(0, 12));

            })
            .catch(error => console.error(error));
    }, []);

    useEffect(() => {
        axios.get('http://localhost:8080/website/topphim')
            .then(response => {
                setMovieTop(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    return (

        <div className="content">

            <br></br>
            <p>Phim đề cử</p>
            <br></br>

            <div id="wrapper">
                <div id="carousel" ref={carouselRef}>
                {Array.isArray(moviesle) &&
                            moviesle.map((moviele, index) => (
                        <div className="container_card" key={index}>
                            <div className="img-container">
                                <span className="vietsub">Tập 19/20 Vietsub</span>
                                <span className="trending">Trending</span>

                                <img
                                    src="https://images.unsplash.com/photo-1534809027769-b00d750a6bac?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1534&q=80"
                                    alt=""
                                />
                            </div>
                            <ul className="social-media">
                                <li>
                                    <a href="#"><PlayCircleRoundedIcon sx={{ color: "#ffffff", width: 50, height: 50 }} />
                                    </a>
                                </li>
                            </ul>
                            <div className="user-info">
                                <h2>Aniket Singh</h2>
                                <span>20-july</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <div className="buttons">
                <button onClick={handleClickGoBack} >
                    <ArrowBackIosNewRoundedIcon />
                </button>
                <button onClick={handleClickGoAhead}>
                    <ArrowForwardIosRoundedIcon />
                </button>
            </div>
            <div className="grid">
                <div className="grid__item grid__item_item4">
                    {/*  */}
                    <p>Phim lẻ mới nhất</p>
                    <br></br>
                    <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        {Array.isArray(moviesle) &&
                            moviesle.map((moviele, index) => (
                                <Grid item xs={3} key={index}>
                                    <div className="card">
                                        <a href="#">
                                            <div className="img1">
                                                <img
                                                    src={`data:image/jpeg;base64,${moviele.image}`}
                                                    alt={moviele.name}
                                                    style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }}
                                                />
                                            </div>

                                            <div className="title">{moviele.name}</div>
                                            <div className="text">Full Vietsub</div>
                                            <div className="catagory">Phát hành năm: {moviele.namphathanh} </div>
                                            <div className="icon_video"><PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} /></div>
                                            <div className="views">{moviele.duration} phút</div>
                                            {/* <div className="icon_video"><PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} /></div> */}
                                        </a>
                                    </div>
                                </Grid>

                            ))}
                    </Grid>
                    <br></br>
                    <hr></hr>
                    <br></br>
                    <p>Phim bộ mới nhất</p>
                    <br></br>
                    <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        {Array.isArray(moviesbo) &&
                            moviesbo.map((moviebo, index) => (
                                <Grid item xs={3} key={index}>
                                    <div className="card">
                                        <a href="#">
                                            <div className="img1">
                                                <img
                                                    src={`data:image/jpeg;base64,${moviebo.image}`}
                                                    alt={moviebo.name}
                                                    style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }}
                                                />
                                            </div>
                                            <div className="title">{moviebo.name}</div>
                                            <div className="text">{moviebo.episodes} Tập Vietsub</div>
                                            <div className="catagory">Phát hành năm: {moviebo.namphathanh} </div>

                                        </a>
                                    </div>
                                </Grid>
                            ))}
                    </Grid>
                    <br></br>
                    <hr></hr>
                    <br></br>
                    <p>Phim chiếu rạp </p>
                    <br></br>
                    <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        {Array.isArray(moviesChieuRap) &&
                            moviesChieuRap.map((movieChieuRap, index) => (
                                <Grid item xs={3} key={index}>
                                    <div className="card">
                                        <a href="#">
                                            <div className="img1">
                                                <img
                                                    src={`data:image/jpeg;base64,${movieChieuRap.image}`}
                                                    alt={movieChieuRap.name}
                                                    style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }}
                                                />                                                </div>
                                            <div className="title">{movieChieuRap.name}</div>
                                            <div className="text">Full Vietsub</div>
                                            <div className="catagory">Phát hành năm: {movieChieuRap.namphathanh} </div>
                                            <div className="icon_video"><PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} /></div>
                                            <div className="views">{movieChieuRap.duration} phút   </div>
                                        </a>
                                    </div>
                                </Grid>
                            ))}
                    </Grid>
                </div>
                <div className="grid__item grid__item_item5">four columns</div>
            </div>
        </div>

    );
}

export default Content;
