import { Link } from "react-router-dom";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import React, { useEffect, useState } from "react";
import axios from 'axios';
import { useParams, useLocation } from "react-router-dom";
import Footer from './footer';
import Tab from './tab';
import MovieTrending from './movietrending';
import OndemandVideoIcon from '@mui/icons-material/OndemandVideo';

function Video() {
    const location = useLocation();
    const id = location.state?.id; // üëà Nh·∫≠n l·∫°i id t·ª´ state

    const [movie, setMovie] = useState(null);
    const [episodes, setEpisodes] = useState([]);
    const [selectedEpisodeUrl, setSelectedEpisodeUrl] = useState('');

    // const currentEpisodeIndex = 0;
    useEffect(() => {
        if (!id) return;

        axios
            .get(`http://localhost:8080/website/movie/${id}`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.error(err));
    }, [id]);

    useEffect(() => {
        if (!id) return;
        axios
            .get(`http://localhost:8080/website/episodesALLByIdMovie/${id}`)
            .then((res) => setEpisodes(res.data))
            .catch((err) => console.error(err));
    }, [id]);

    const episodeName = episodes.length > 0
        ? episodes.reduce((min, ep) => ep.name < min ? ep.name : min, episodes[0].name)
        : '';
    // console.log(episodeName)
    if (!movie) return <div>Loading...</div>;

    return (

        <div>
            <NavBar />
            <br></br>



            <div className="content">
                <div className="link">
                    <HomeIcon sx={{ color: "#8B4513", fontSize: 25 }} />
                    {(movie.style.name === 'Phim l·∫ª' || movie.style.name === 'Phim chi·∫øu r·∫°p') && (
                        <>
                            <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name} / {movie.episodes === 1 ? 'T·∫≠p Full' : `${movie.episodes}`}
                        </>
                    )}
                    {movie.style.name === 'Phim b·ªô' && (
                        <>
                            <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name} / {`T·∫≠p ${episodeName}`}
                        </>
                    )}
                </div>
                <div className="grid">
                    <div className="grid__item grid__item_item4">
                        {(movie.style.name === 'Phim l·∫ª' || movie.style.name === 'Phim chi·∫øu r·∫°p') && movie.link && (
                            <iframe
                                src={movie.link}
                                width="100%"
                                height="500"
                                allowFullScreen
                                className="blur-filter rounded-xl shadow"
                                title="Tr√¨nh ph√°t video"
                            />
                        )}
                        {movie.style.name === 'Phim b·ªô' && (
                            <iframe
                                width="100%"
                                height="500"
                                allowFullScreen
                                className="blur-filter rounded-xl shadow"
                                title="Tr√¨nh ph√°t video"
                            />
                        )}
                        <br></br>
                        <br></br>
                        <div className="link"><OndemandVideoIcon sx={{ color: "#8B4513", fontSize: 25 }} /> #(Vietsub)</div>

                        <div>
                            {(movie.style.name === 'Phim l·∫ª' || movie.style.name === 'Phim chi·∫øu r·∫°p') && (
                                <>
                                    <button className="button_episodes">Full</button>
                                </>
                            )}
                            {movie.style.name === 'Phim b·ªô' && (
                                <>
                                    {Array.isArray(episodes) &&
                                        episodes.map((episode, index) => (
                                            <button data-url={episode.link} className="button_episodes">{episode.name}</button>
                                        ))}

                                </>
                            )}
                        </div>




                        <br></br>
                        <div className="infor_flim">
                            <h1>{movie.name}</h1>
                            <hr></hr>
                            <br></br>
                            {movie.description}


                        </div>
                        <br></br>

                        <div className="comment__container">
                            <b className="comment__title">0 comment</b>
                            <hr></hr>
                            <br></br>
                            <div className="comment__body">
                                <img src="https://img.ophim.live/uploads/movies/yeu-la-mu-quang-brazil-phan-3-thumb.jpg"
                                    style={{ width: "50px", height: "50px" }}
                                    className="comment__avatar" alt="avatar" />
                                <div>
                                    <textarea className="comment__textarea" placeholder="B√¨nh lu·∫≠n ..." rows="8"></textarea>
                                    <div className="comment__post">
                                        <div className="comment__info"></div>
                                        <div>
                                            <button className="comment__send">Post Comment</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <br></br>
                        <p>Phim ƒë·ªÅ c·ª≠</p>
                        <br></br>
                        <MovieTrending />

                    </div>
                    <div className="grid__item grid__item_item5">
                        <Tab />
                    </div>
                </div>
            </div>
            <Footer />

        </div>
    );
}

export default Video;
