import React, { useEffect, useState } from "react";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import ListTrending from './listrending';
import slugify from "./utils/slugify";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import { Link, useLocation } from "react-router-dom";

function PhimLe() {
  const navigate = useNavigate();
  const location = useLocation();
  const id = location.state?.id;
  const [movies, setMovie] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;

  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:8080/website/movie/styleId/${id}`)
      .then((res) => setMovie(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  const isPhimBo = movies.length > 0 && movies[0].style.name === 'Phim bộ';

  const phimBoMovies = isPhimBo ? movies : [];
  const otherMovies = isPhimBo ? [] : movies;

  const totalPages = isPhimBo ? Math.ceil(phimBoMovies.length / itemsPerPage) : 1;
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentMovies = isPhimBo
    ? phimBoMovies.slice(startIndex, endIndex)
    : otherMovies;

  // Tạo danh sách trang cần hiển thị không trùng lặp
  const getPaginationPages = () => {
    const pages = [];

    if (totalPages <= 5) {
      for (let i = 1; i <= totalPages; i++) pages.push(i);
    } else {
      if (currentPage > 2) pages.push(1);
      if (currentPage > 3) pages.push('...');
      if (currentPage > 1) pages.push(currentPage - 1);
      pages.push(currentPage);
      if (currentPage < totalPages) pages.push(currentPage + 1);
      if (currentPage < totalPages - 2) pages.push('...');
      if (currentPage < totalPages - 1) pages.push(totalPages);
    }

    return pages.filter((val, i, arr) => arr.indexOf(val) === i); // remove duplicate
  };

  return (
    <div>
      <NavBar />
      <br />
      <div className="content">
        <div className="link">
          <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25 }} />
          {movies.length > 0 && (
            <div>
              <Link to="/home" className="xemphim">Xem phim</Link> / {movies[0].style.name}
            </div>
          )}
        </div>
        <div className="grid">
          <div className="grid__item grid__item_item4">
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
              {currentMovies.map((movie, index) => (
                <Grid item xs={3} key={index}>
                  <div
                    className="card"
                    onClick={() =>
                      navigate(`/film/${slugify(movie.name)}`, {
                        state: { id: movie.id },
                      })
                    }
                  >
                    <a href="#">
                      <div className="img1">
                        <img
                          src={`data:image/jpeg;base64,${movie.image}`}
                          alt={movie.name}
                          style={{
                            width: "100%",
                            height: "100%",
                            objectFit: "cover",
                            borderRadius: "8px",
                          }}
                        />
                      </div>
                      <div className="title">{movie.name}</div>
                      <div className="text">
                        {movie.style.name === 'Phim bộ'
                          ? `${movie.episodes} Tập Vietsub`
                          : 'Full Vietsub'}
                      </div>
                      <div className="catagory">Phát hành năm: {movie.namphathanh}</div>
                      <div className="icon_video">
                        <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                      </div>
                      {movie.style.name !== 'Phim bộ' && (
                        <div className="views">{movie.duration} phút</div>
                      )}
                    </a>
                  </div>
                </Grid>
              ))}
            </Grid>

            {/* PHÂN TRANG CHỈ CHO PHIM BỘ */}
            {isPhimBo && totalPages > 1 && (
              <div className="pagination">
                {getPaginationPages().map((page, i) =>
                  page === '...' ? (
                    <span key={`dots-${i}`} className="dots">...</span>
                  ) : (
                    <button
                      key={page}
                      onClick={() => setCurrentPage(page)}
                      className={page === currentPage ? 'active' : ''}
                    >
                      {page}
                    </button>
                  )
                )}
              </div>
            )}
          </div>
          <div className="grid__item grid__item_item5">
            <ListTrending />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PhimLe;
