import { Link } from "react-router-dom";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import React, { useEffect, useState } from "react";
import axios from 'axios';
import { useParams, useLocation } from "react-router-dom";
import Footer from './footer';
import Tab from './tab';
import MovieTrending from './movietrending';

function Video() {
    const location = useLocation();
    const id = location.state?.id; // 👈 Nhận lại id từ state

    const [movie, setMovie] = useState(null);
    const [episodes, setEpisodes] = useState([]);
    const currentEpisodeIndex = 0;
    useEffect(() => {
        if (!id) return;

        axios
            .get(`http://localhost:8080/website/movie/${id}`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.error(err));
    }, [id]);

    useEffect(() => {
        if (!id) return;
        axios
            .get(`http://localhost:8080/website/movieALLEpisodes/${id}`)
            .then((res) => setEpisodes(res.data))
            .catch((err) => console.error(err));
    }, [id]);
    const episodeName = episodes.length > 0 ? episodes[currentEpisodeIndex]?.name : '';
    const episodeSlug = episodeName ? `tap-so-${episodeName}` : '';
    if (!movie) return <div>Loading...</div>;
    const video = document.querySelector("#video");
    const videoWrapper = document.querySelector("#video-wrapper");
    const controls = document.querySelector("#controls");
    
    const playBtn = document.querySelector("#play-pause");
    const volumeBtn = document.querySelector("#mute");
    const fullScreenBtn = document.querySelector("#full-screen");
    const timeline = document.querySelector("#timeline");
    const progress = document.querySelector("#progress");
    const currentTimeElem = document.querySelector("#current-time");
    const durationElem = document.querySelector("#duration");
    
    let isDragging = false;
    
    const togglePlayState = () => {
      if (video.paused || video.ended) {
        video.play();
        playBtn.classList.add("isPlaying");
      } else {
        video.pause();
        playBtn.classList.remove("isPlaying");
      }
    };
    
    video.addEventListener("click", togglePlayState);
    video.addEventListener("ended", () => {
      playBtn.classList.remove("isPlaying");
      showControls()
    });
    
    playBtn.addEventListener("click", togglePlayState);
    fullScreenBtn.addEventListener("click", () => {
      console.log("Make Full Screen");
    });
    
    const updateProgress = () => {
      if (!isDragging) {
        const percentage = (video.currentTime / video.duration) * 100;
        progress.style.width = percentage + '%';
        updateTimeDisplay();
      }
    }
    
    const updateTimeDisplay = () => {
      const currentMinutes = Math.floor(video.currentTime / 60);
      const currentSeconds = Math.floor(video.currentTime % 60);
      const durationMinutes = Math.floor(video.duration / 60);
      const durationSeconds = Math.floor(video.duration % 60);
    
      currentTimeElem.textContent = `${currentMinutes}:${currentSeconds < 10 ? "0" : ""}${currentSeconds}`;
      durationElem.textContent = `${durationMinutes}:${durationSeconds < 10 ? "0" : ""}${durationSeconds}`;
    }
    
    video.addEventListener('timeupdate', updateProgress);
    video.addEventListener("loadedmetadata", () => {
      updateTimeDisplay();
    });
    
    timeline.addEventListener('click', (event) => {
      const timelineWidth = timeline.offsetWidth;
      const clickX = event.offsetX;
      const newTime = (clickX / timelineWidth) * video.duration;
      video.currentTime = newTime;
    });
    
    const startDrag = (event) => {
      isDragging = true;
      updateTimeline(event.touches ? event.touches[0] : event);
    }
    
    const drag = (event) => {
      if (isDragging) {
        updateTimeline(event.touches ? event.touches[0] : event);
      }
    }
    
    const stopDrag = (event) => {
      if (isDragging) {
        isDragging = false;
        updateTimeline(event.changedTouches ? event.changedTouches[0] : event);
        video.currentTime = ((event.changedTouches ? event.changedTouches[0].clientX : event.clientX) - timeline.getBoundingClientRect().left) / timeline.offsetWidth * video.duration;
      }
    }
    
    const updateTimeline = (event) => {
      const rect = timeline.getBoundingClientRect();
      let offsetX = event.clientX - rect.left;
    
      if (offsetX < 0) offsetX = 0;
      if (offsetX > rect.width) offsetX = rect.width;
    
      const percentage = (offsetX / rect.width) * 100;
      progress.style.width = percentage + '%';
    
      const newTime = (offsetX / rect.width) * video.duration;
      updateTimeDisplay(newTime);
      
      if (isDragging) {
        video.currentTime = newTime;
      }
    }
    
    timeline.addEventListener('mousedown', startDrag);
    document.addEventListener('mouseup', stopDrag);
    document.addEventListener('mousemove', drag);
    
    timeline.addEventListener('touchstart', startDrag);
    document.addEventListener('touchend', stopDrag);
    document.addEventListener('touchmove', drag);
    
    const toggleVolume = () => {
      if (video.muted) {
        video.muted = false
        volumeBtn.classList.remove("isMute");
      } else {
        video.muted = true
        volumeBtn.classList.add("isMute");
      }
    };
    
    volumeBtn.addEventListener("click", toggleVolume)
    
    let fullScreenActive = false
    
    const toggleFullscreen = () => {
      if (fullScreenActive) {
        fullScreenActive = false;
        fullScreenBtn.classList.remove("isFullscreen");
        closeFullscreen();
      } else {
        fullScreenActive = true;
        fullScreenBtn.classList.add("isFullscreen");
        openFullscreen();
      }
    };
    
    const openFullscreen = () => {
      if (videoWrapper.requestFullscreen) {
        videoWrapper.requestFullscreen();
      } else if (elem.webkitRequestFullscreen) {
        videoWrapper.webkitRequestFullscreen();
      } else if (elem.msRequestFullscreen) {
        videoWrapper.msRequestFullscreen();
      }
    }
    
    const closeFullscreen = () => {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      } else if (document.webkitExitFullscreen) {
        document.webkitExitFullscreen();
      } else if (document.msExitFullscreen) {
        document.msExitFullscreen();
      }
    }
    
    fullScreenBtn.addEventListener("click", toggleFullscreen)
    
    let mouseActivityTimeout;
    
    const showControls = () => {
      controls.classList.remove('hide-controls');
      resetMouseActivityTimeout();
    }
    
    const hideControls = () => {
      if (!video.paused && !video.ended) {
        controls.classList.add('hide-controls');
      }
    }
    
    const resetMouseActivityTimeout = () => {
      clearTimeout(mouseActivityTimeout);
      if (!video.paused && !video.ended) {
        mouseActivityTimeout = setTimeout(() => {
          hideControls();
        }, 3000);
      }
    }
    
    videoWrapper.addEventListener('mousemove', showControls);
    
    video.addEventListener('pause', showControls);
    video.addEventListener('play', resetMouseActivityTimeout);
    
    videoWrapper.addEventListener('mouseenter', showControls);
    videoWrapper.addEventListener('mouseleave', hideControls);
    
    controls.addEventListener('mousemove', showControls);
    controls.addEventListener('mouseenter', showControls);
    controls.addEventListener('mouseleave', resetMouseActivityTimeout);
    
    videoWrapper.addEventListener('touchstart', showControls);
    videoWrapper.addEventListener('touchend', () => {
      setTimeout(hideControls, 3000);
    });
    
    resetMouseActivityTimeout();
    return (

        <div>
            <NavBar />
            <br></br>
            <div className="link">
                <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25, marginLeft: "150px" }} />
                {(movie.style.name === 'Phim lẻ' || movie.style.name === 'Phim chiếu rạp') && (
                    <>
                        <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name} / {movie.episodes === 1 ? 'Tập Full' : `${movie.episodes}`}
                    </>
                )}
                {movie.style.name === 'Phim bộ' && (
                    <>
                        <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name} / {`Tập ${episodeName}`}
                    </>
                )}
            </div>


            <div className="content">
                <div className="grid">
                    <div className="grid__item grid__item_item4">
                        <div id="video-wrapper">
                            <video id="video" src="https://videos.ctfassets.net/fd9eedfc5vaw/5GZ6peCztE22La6zrruXHH/9ba11a7d28c89ca9d94953a5d4f252a0/cn-halloween-animation-2023.mp4" muted autoplay></video>
                            <div id="controls" class="control-wrapper">
                                <div class="controls">
                                    <button id="play-pause" class="isPlaying">
                                        <img class="playIcon" src="https://cdn-icons-png.flaticon.com/512/4211/4211344.png" alt="play" />
                                        <img class="pauseIcon" src="https://cdn-icons-png.flaticon.com/512/4211/4211362.png" alt="pause" />
                                    </button>
                                    <div class="timeline-container">
                                        <div id="timeline" class="timeline-spacer">
                                            <div class="full-timeline">
                                                <div id="progress"></div>
                                            </div>
                                        </div>
                                        <span id="current-time">0:00</span> / <span id="duration">0:00</span>
                                    </div>
                                    <button id="mute" class="isMute">
                                        <img class="muteIcon" src="https://cdn-icons-png.flaticon.com/512/4211/4211419.png" alt="mute"/>
                                            <img class="volumeIcon" src="https://cdn-icons-png.flaticon.com/512/4211/4211413.png" alt="volume"/>
                                            </button>
                                            <button id="full-screen">
                                                <img class="fullscreenIcon" src="https://cdn-icons-png.flaticon.com/512/4211/4211514.png" alt="fullscreen"/>
                                                    <img class="minimiseIcon" src="https://cdn-icons-png.flaticon.com/512/4211/4211518.png" alt="minimise"/>
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                        <br></br>
                                        <div className="infor_flim">
                                            <h1>Nội dung phim</h1>
                                            <hr></hr>
                                            <br></br>
                                            {movie.description}
                                        </div>
                                        <br></br>

                                        <div className="comment__container">
                                            <b className="comment__title">0 comment</b>
                                            <hr></hr>
                                            <br></br>
                                            <div className="comment__body">
                                                <img src="https://img.ophim.live/uploads/movies/yeu-la-mu-quang-brazil-phan-3-thumb.jpg"
                                                    style={{ width: "50px", height: "50px" }}
                                                    className="comment__avatar" alt="avatar" />
                                                <div>
                                                    <textarea className="comment__textarea" placeholder="Bình luận ..." rows="8"></textarea>
                                                    <div className="comment__post">
                                                        <div className="comment__info"></div>
                                                        <div>
                                                            <button className="comment__send">Post Comment</button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>

                                        <br></br>
                                        <p>Phim đề cử</p>
                                        <br></br>
                                        <MovieTrending />

                                </div>
                                <div className="grid__item grid__item_item5">
                                    <Tab />
                                </div>
                            </div>
                        </div>
                        <Footer />

                    </div>
                    );
}

                    export default Video;
