import React, { useEffect, useState } from 'react';
import axios from 'axios'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { Paper, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit'
import TextField from '@mui/material/TextField';
import TablePagination from '@mui/material/TablePagination';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Autocomplete from '@mui/material/Autocomplete';
import { TextareaAutosize as BaseTextareaAutosize } from '@mui/base/TextareaAutosize';
import { styled } from '@mui/system';



function Movie() {
    const [movies, setMovie] = useState(null)
    const [deleteId, setDeleteId] = useState(null)
    const [confirmOpen, setConfirmOpen] = useState(false)
    const [openEdit, setOpenEdit] = useState(false)
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [image, setImage] = useState(null);
    const [types, setType] = useState(null)
    const [selectedType, setSelectedType] = useState(null); // Lưu id của loại đã chọn
    const [styles, setStyle] = useState(null)
    const [selectedStyle, setSelectedStyle] = useState(null);
    // const [snackbarOpen, setSnackbarOpen] = useState(false)
    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (e) => setImage(e.target.result);
            reader.readAsDataURL(file);
        }
    };
    const handleRemoveImage = () => {
        setImage(null);
        document.getElementById("file-input").value = "";
    };
    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };


    const [newMovie, setNewMovie] = useState({
        name: ''
    })
    const [editMovie, setEditMovie] = useState({
        name: ''
    })
    const handleClickOpenEdit = (movie) => {
        setEditMovie(movie)
        setOpenEdit(true)
    }

    const handleChange = (e) => {
        setNewMovie({ ...newMovie, name: e.target.value });
    };
    const handleConfirmOpen = (id) => {
        setDeleteId(id)
        setConfirmOpen(true)
    }
    const handleConfirmClose = (id) => {
        setDeleteId(null)
        setConfirmOpen(false)
    }
    const handleCloseEdit = () => {
        setOpenEdit(false)
    }

    const handleChangeEdit = (event) => {
        setEditMovie({
            ...editMovie,
            [event.target.name]: event.target.value
        });
    };


    // delete
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/website/movie/${id}`);
            setMovie(movies.filter(movie => movie.id !== id));
            handleConfirmClose()
        } catch (error) {
            alert('There was an error deleting the style')
        }
    };
    // add
    const handleAdd = async () => {
        try {
            if (!newMovie.name.trim()) {
                window.alert("Vui lòng nhập tên name!");
                return;
            }
            // Kiểm tra xem tên đã tồn tại trong danh sách chưa
            const isDuplicate = movies.some(movie => movie.name === newMovie.name.trim());
            if (isDuplicate) {
                window.alert("Movie này đã tồn tại!");
                return;
            }

            const response = await axios.post('http://localhost:8080/website/movie', newMovie);

            setMovie([...movies, response.data]);
            setNewMovie({ name: '' });

        } catch (error) {
            console.error("Lỗi khi thêm loại:", error);
        }
    };

    // Update
    const handleEditMovie = async () => {
        try {

            const response = await axios.put(`http://localhost:8080/website/movie/${editMovie.id}`, editMovie);

            setMovie(prevMovies =>
                prevMovies.map(movie =>
                    movie.id === editMovie.id ? response.data : movie
                )
            );

            handleCloseEdit();
        } catch (error) {
            console.error('Lỗi xảy ra khi cập nhật:', error);
        }
    };
    useEffect(() => {
        axios.get('http://localhost:8080/website/movieAll')
            .then(response => {
                setMovie(response.data);
            })
            .catch(error => console.error(error));
    }, []);
    useEffect(() => {
        axios.get('http://localhost:8080/website/typeAll')
            .then(response => {
                setType(response.data);
            })
            .catch(error => console.error(error));
    }, []);

    return (
        <Box
            component="form"
            sx={{ '& > :not(movie)': { maxWidth: '100%' } }}
            noValidate
            autoComplete="off"
        >
            <section className="page-content">
                <h1>Movie</h1>
                <hr />
                <br />
                <div className="container">
                    <div className="row">
                        <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Name Nation ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Duration ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Episodes ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Year of issue ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Trailer ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Cast ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Directors ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                            <Autocomplete
                                    disablePortal
                                    options={sty} // Truyền danh sách đối tượng đầy đủ
                                    getOptionLabel={(option) => option.name} // Hiển thị name trong dropdown
                                    onChange={(event, newValue) => {
                                        setSelectedType(newValue ? newValue.id : null); // Lưu ID khi chọn
                                    }}
                                    fullWidth
                                    renderInput={(params) => <TextField {...params} label="Name Type ..." />}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <Autocomplete
                                    disablePortal
                                    options={types} // Truyền danh sách đối tượng đầy đủ
                                    getOptionLabel={(option) => option.name} // Hiển thị name trong dropdown
                                    onChange={(event, newValue) => {
                                        setSelectedType(newValue ? newValue.id : null); // Lưu ID khi chọn
                                    }}
                                    fullWidth
                                    renderInput={(params) => <TextField {...params} label="Name Type ..." />}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <Autocomplete
                                    disablePortal
                                    // options={}
                                    fullWidth
                                    renderInput={(params) => <TextField {...params} label="Name Nation ..." />}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextField
                                    id="outlined-multiline-flexible"
                                    label="Link Film ..."
                                    multiline
                                    fullWidth
                                    maxRows={10}
                                    value={newMovie.name}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <Autocomplete
                                    disablePortal
                                    options={["Đang chiếu", "Chưa chiếu"]}
                                    fullWidth
                                    renderInput={(params) => <TextField {...params} label="Trạng thái ..." />}
                                />
                            </Grid>
                            <Grid item xs={6}>
                                <TextareaAutosize aria-label="empty textarea" placeholder="Empty" />
                            </Grid>
                            <Grid item xs={6}>
                                <div className="mb-1">
                                    Image <span className="font-css top">*</span>
                                    <div>
                                        <input type="file" id="file-input" name="ImageStyle" onChange={handleImageChange} />
                                    </div>
                                    <br></br>
                                    {image && (
                                        <div className="mt-2">
                                            <img style={{ width: '100px' }} src={image} alt="Selected" />
                                            <button onClick={handleRemoveImage} color="primary" >
                                                Delete image
                                            </button>
                                        </div>
                                    )}
                                </div>
                            </Grid>
                        </Grid>

                    </div>
                    <br></br>
                    <div className="row">
                        <Button onClick={handleAdd} color="primary" variant="contained">
                            Add
                        </Button>
                    </div>
                    <br></br>
                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow style={{ backgroundColor: 'Gray' }}>
                                    <TableCell align="center">STT</TableCell>
                                    <TableCell align="center">Name Style</TableCell>
                                    <TableCell align="center">Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {Array.isArray(movies) && movies.length > 0 ? (
                                    movies.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((movie, index) => (
                                        <TableRow key={movie.id}>
                                            <TableCell align="center">{page * rowsPerPage + index + 1}</TableCell>
                                            <TableCell align="center">{movie.name}</TableCell>
                                            <TableCell align="center">
                                                <IconButton color="primary" onClick={() => handleConfirmOpen(movie.id)}>
                                                    <DeleteIcon />
                                                </IconButton>
                                                <IconButton color="primary" onClick={() => handleClickOpenEdit(movie)}>
                                                    <EditIcon />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                    ))
                                ) : (
                                    <TableRow>
                                        <TableCell colSpan={3} align="center">Loading...</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 10, 25]}
                            component="div"
                            count={movies != null ? movies.length : 0}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </TableContainer>


                    {/* Xoa */}
                    <Dialog open={confirmOpen} onClose={handleConfirmClose}>
                        <DialogTitle>Confirm DeleteId</DialogTitle>
                        <DialogContent>Are you sure you want to delete this Style?</DialogContent>
                        <DialogActions>
                            <Button onClick={handleConfirmClose} color='primary'>
                                Cancel
                            </Button>
                            <Button onClick={() => { handleDelete(deleteId); }} color='secondary' variant='contained'>
                                Delete
                            </Button>
                        </DialogActions>
                    </Dialog>
                    {/* edit */}
                    <Dialog open={openEdit} onClose={handleCloseEdit}>
                        <DialogTitle>Update Movie</DialogTitle>
                        <DialogContent>
                            <TextField
                                name="name"
                                type="text"
                                value={editMovie.name || ''}
                                onChange={handleChangeEdit}
                            />
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleCloseEdit} color="primary">
                                Cancel
                            </Button>
                            <Button color="primary" variant="contained" onClick={handleEditMovie}>
                                Update
                            </Button>
                        </DialogActions>
                    </Dialog>
                </div>

            </section>
        </Box>
    )
}
const blue = {
    100: '#DAECFF',
    200: '#b6daff',
    400: '#3399FF',
    500: '#007FFF',
    600: '#0072E5',
    900: '#003A75',
};

const grey = {
    50: '#F3F6F9',
    100: '#E5EAF2',
    200: '#DAE2ED',
    300: '#C7D0DD',
    400: '#B0B8C4',
    500: '#9DA8B7',
    600: '#6B7A90',
    700: '#434D5B',
    800: '#303740',
    900: '#1C2025',
};

const TextareaAutosize = styled(BaseTextareaAutosize)(
    ({ theme }) => `
    box-sizing: border-box;
    width: 590px;
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 0.875rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 8px 12px;
    border-radius: 8px;
    color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
    background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
    border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
    box-shadow: 0 2px 2px ${theme.palette.mode === 'dark' ? grey[900] : grey[50]};
  
    &:hover {
      border-color: ${blue[400]};
    }
  
    &:focus {
      border-color: ${blue[400]};
      box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[600] : blue[200]};
    }
  
    /* firefox */
    &:focus-visible {
      outline: 0;
    }
  `,
);
export default Movie;
