                                maxRows={10}
                                value={editMovie.directors || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <Autocomplete
                                multiple
                                id="checkboxes-tags-demo"
                                options={types || []} // Đảm bảo không bị lỗi nếu types chưa có dữ liệu
                                disableCloseOnSelect
                                getOptionLabel={(option) => option.name}
                                value={selectedType} // Hiển thị danh sách thể loại đã chọn
                                onChange={(event, newValue) => {
                                    setSelectedType(newValue);
                                    setEditMovie(prev => ({
                                        ...prev,
                                        types: newValue // Cập nhật type cho movie
                                    }));
                                }}
                                renderOption={(props, option, { selected }) => (
                                    <li {...props} key={option.id}>
                                        <Checkbox
                                            icon={icon}
                                            checkedIcon={checkedIcon}
                                            style={{ marginRight: 8 }}
                                            checked={selected}
                                        />
                                        {option.name}
                                    </li>
                                )}
                                fullWidth
                                renderInput={(params) => (
                                    <TextField {...params} label="Name Type.." placeholder="Select Types ..." />
                                )}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <Autocomplete
                                disablePortal
                                options={nations}
                                getOptionLabel={(option) => option.name}
                                value={selectedNation} // Gán giá trị từ state
                                onChange={(event, newValue) => {
                                    setSelectedNation(newValue); // Cập nhật giá trị hiển thị
                                    setEditMovie((prev) => ({
                                        ...prev,
                                        nation: newValue ? newValue.id : null, // Lưu ID vào state của phim
                                    }));
                                }}
                                name="nation"
                                fullWidth
                                renderInput={(params) => <TextField {...params} label="Name Nation ..." />}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <Autocomplete
                                disablePortal
                                options={styles}
                                getOptionLabel={(option) => option.name}
                                value={selectedStyle}
                                onChange={(event, newValue) => {
                                    setSelectedStyle(newValue);
                                    setNewMovie((prev) => ({
                                        ...prev,
                                        style: newValue ? newValue.id : null,
                                    }));
                                }}
                                name='style'
                                fullWidth
                                renderInput={(params) => <TextField {...params} label="Name Style ..." />}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                id="outlined-multiline-flexible"
                                label="Episodes ..."
                                multiline
                                fullWidth
                                name="episodes"
                                maxRows={10}
                                value={editMovie.episodes || ''}
                                onChange={handleChangeEdit}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <Autocomplete
                                disablePortal
                                options={[
                                    { label: "Đang chiếu", value: "0" },
                                    { label: "Chưa chiếu", value: "1" },
                                ]}
                                fullWidth
                                name="status"
                                value={
                                    editMovie.status !== undefined
                                        ? [
                                            { label: "Đang chiếu", value: "0" },
                                            { label: "Chưa chiếu", value: "1" }
                                        ].find(option => option.value === String(editMovie.status)) || null
                                        : null
                                }
                                onChange={(event, newValue) => {
                                    setEditMovie((prev) => ({ ...prev, status: newValue ? newValue.value : "" }));
                                }}
                                getOptionLabel={(option) => option?.label || ""}
                                renderInput={(params) => (
                                    <TextField {...params} label="Status ..." />
                                )}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextareaAutosize
                                name="description"
                                // value={newMovie.description}
                                value={editMovie.description || ''}
                                onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })}
                                aria-label="empty textarea"
                                placeholder="Description ..."
                                minRows={3}
                                style={{
                                    width: "100%",
                                    padding: "10px",
                                    fontSize: "16px",
                                    borderRadius: "5px",
                                    border: "1px solid #ccc",
                                    outline: "none",
                                    resize: "vertical"
                                }}
                            />

                        </Grid>

                        <Grid item xs={6}>
                            {/* <br></br> */}
                            <div className="mb-1">
                                Image <span className="font-css top">*</span>
                                <div>
                                    <input type="file" name="image" id="file-input" onChange={handleImageChange} />
                                </div>
                                <br />

                                {/* Kiểm tra nếu có ảnh thì hiển thị */}
                                {imagePreview || editMovie?.image ? (
                                    <div className="mt-2 flex items-center gap-2">
                                        <img
                                            src={imagePreview || `data:image/jpeg;base64,${editMovie.image}`}
                                            alt="Movie"
                                            className="w-16 h-16 object-cover rounded-lg border"
                                            style={{ width: "100px", height: "100px", objectFit: "cover", borderRadius: "8px" }}
                                        />
                                        {/* <button
                                            onClick={handleDeleteImage}
                                            className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                                        >
                                            Delete Image
                                        </button> */}
                                    </div>
                                ) : null}
                            </div>

                        </Grid>


                    </Grid>


                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseEdit2} color="primary">
                        Cancel
                    </Button>
                    <Button color="primary" variant="contained" onClick={handleEditMovie}>
                        Update
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Update episodes */}
            <Dialog open={openEditEpisode} sx={{ '& .MuiDialog-paper': { width: '700px' } }} >
                <DialogTitle>Update Style</DialogTitle>
                <DialogContent>
                    <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                name="name"
                                type="text"
                                value={editEpisode.name || ''}
                                onChange={(e) => {
                                    const value = e.target.value;
                                    if (/^\d*$/.test(value)) {
                                        handleChangeEditEpisode(e);
                                    } else {
                                        alert("Vui lòng nhập số!");
                                    }
                                }}

                            />
                        </Grid>
                        <Grid item xs={6}>
                            <br></br>
                            <TextField
                                name="link"
                                type="text"
                                value={editEpisode.link || ''}
                                onChange={handleChangeEditEpisode}
                            />
                        </Grid>
                    </Grid>

                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseEditEposide} color="primary">
                        Cancel
                    </Button>
                    <Button color="primary" variant="contained" onClick={handleEditEpisodes}>
                        Update
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    )
}
const blue = {
    100: '#DAECFF',
    200: '#b6daff',
    400: '#3399FF',
    500: '#007FFF',
    600: '#0072E5',
    900: '#003A75',
};

const grey = {
    50: '#F3F6F9',
    100: '#E5EAF2',
    200: '#DAE2ED',
    300: '#C7D0DD',
    400: '#B0B8C4',
    500: '#9DA8B7',
    600: '#6B7A90',
    700: '#434D5B',
    800: '#303740',
    900: '#1C2025',
};

const TextareaAutosize = styled(BaseTextareaAutosize)(
    ({ theme }) => `
    box-sizing: border-box;
    width: 460px;
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 0.875rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 8px 12px;
    border-radius: 8px;
    color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
    background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
    border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
    box-shadow: 0 2px 2px ${theme.palette.mode === 'dark' ? grey[900] : grey[50]};
  
    &:hover {
      border-color: ${blue[400]};
    }
  
    &:focus {
      border-color: ${blue[400]};
      box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[600] : blue[200]};
    }
  
    /* firefox */
    &:focus-visible {
      outline: 0;
    }
  `,
);
export default TableMovie;
