import { useState, useEffect } from "react";
import axios from "axios";
import Grid from "@mui/material/Grid";
import PlayCircleRoundedIcon from "@mui/icons-material/PlayCircleRounded";

const MovieList = () => {
  const [moviesle, setMovieLe] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/website/movieLe")
      .then((response) => {
        setMovieLe(response.data);
      })
      .catch((error) => console.error(error));
  }, []);

  return (
    <Grid container spacing={2}>
      {Array.isArray(moviesle) &&
        moviesle.map((moviele, index) => (
          <Grid item xs={3} key={index}>
            <div className="card">
              <a href="#">
                <div className="img1">
                  <img src="https://upload.wikimedia.org/wikipedia/en/2/2c/A_Babysitter%27s_Guide_to_Monster_Hunting.jpg" />
                </div>
                <div className="title">
                  A Babysitter’s Guide to Monster Hunting
                </div>
                <div className="text">Full Vietsub</div>
                <div className="category">Phát hành năm: 2002</div>
                <div className="icon_video">
                  <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                </div>
                <div className="views">191 phút</div>
              </a>
            </div>
          </Grid>
        ))}
    </Grid>
  );
};

export default MovieList;
