import React, { useEffect, useState } from "react";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import Grid from '@mui/material/Grid';
import '../css/navbar.css';
import axios from 'axios';
import { useNavigate, useLocation, Link } from "react-router-dom";
import ListTrending from './listrending';
import slugify from "./utils/slugify";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';

function PhimListStyle() {
  const navigate = useNavigate();
  const location = useLocation();
  const id = location.state?.id;

  const [movies, setMovies] = useState([]);
  const [types, setTypes] = useState([]);
  const [nations, setNations] = useState([]);

  const [value, setValue] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [valueType, setValueType] = useState(null);
  const [inputValueType, setInputValueType] = useState('');
  const [valueNation, setValueNation] = useState(null);
  const [inputValueNation, setInputValueNation] = useState('');

  const [hasFiltered, setHasFiltered] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 20;

  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: currentYear - 1970 + 1 }, (_, i) => (1970 + i).toString());

  // Lấy danh sách thể loại và quốc gia
  useEffect(() => {
    axios.get('http://localhost:8080/website/typeAll')
      .then(res => setTypes(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:8080/website/nationAll')
      .then(res => setNations(res.data))
      .catch(err => console.error(err));
  }, []);

  // Lọc phim
  const filterMovies = () => {
    axios.get('http://localhost:8080/website/filter', {
      params: {
        styleId: id,
        nationId: valueNation?.id,
        typeId: valueType?.id,
        namPhatHanh: value || '',
      }
    })
    .then(res => {
      setMovies(res.data);
      setHasFiltered(true);
      setCurrentPage(1); // reset về trang đầu
    })
    .catch(err => console.error("Error filtering movies:", err));
  };

  // Tính toán phim hiển thị theo trang
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentMovies = movies.slice(startIndex, startIndex + itemsPerPage);
  const totalPages = Math.ceil(movies.length / itemsPerPage);

  return (
    <div>
      <NavBar />
      <div className="content">
        <div className="link">
          <HomeIcon sx={{ color: "#8B4513", fontSize: 25 }} />
          {hasFiltered && movies.length > 0 && (
            <div>
              <Link to="/home" className="xemphim">Xem phim</Link> / {movies[0].style.name}
            </div>
          )}
        </div>

        {/* Bộ lọc */}
        <div className="shearch">
          <div className="items">
            <div className="item">
              <Autocomplete
                options={types}
                getOptionLabel={(type) => type.name || ''}
                value={valueType}
                onChange={(e, newValue) => setValueType(newValue)}
                inputValue={inputValueType}
                onInputChange={(e, newInputValue) => setInputValueType(newInputValue)}
                sx={{ width: 300 }}
                renderInput={(params) => (
                  <TextField {...params} label="Thể loại" variant="outlined"
                    InputProps={{ ...params.InputProps, sx: { color: '#e6e6e6', backgroundColor: '#2a2a2a' } }}
                    InputLabelProps={{ sx: { color: '#e6e6e6' } }}
                  />
                )}
              />
            </div>
            <div className="item">
              <Autocomplete
                options={nations}
                getOptionLabel={(nation) => nation.name || ''}
                value={valueNation}
                onChange={(e, newValue) => setValueNation(newValue)}
                inputValue={inputValueNation}
                onInputChange={(e, newInputValue) => setInputValueNation(newInputValue)}
                sx={{ width: 300 }}
                renderInput={(params) => (
                  <TextField {...params} label="Quốc gia" variant="outlined"
                    InputProps={{ ...params.InputProps, sx: { color: '#e6e6e6', backgroundColor: '#2a2a2a' } }}
                    InputLabelProps={{ sx: { color: '#e6e6e6' } }}
                  />
                )}
              />
            </div>
            <div className="item">
              <Autocomplete
                options={years}
                value={value}
                onChange={(e, newValue) => setValue(newValue)}
                inputValue={inputValue}
                onInputChange={(e, newInputValue) => setInputValue(newInputValue)}
                sx={{ width: 300 }}
                renderInput={(params) => (
                  <TextField {...params} label="Năm" variant="outlined"
                    InputProps={{ ...params.InputProps, sx: { color: '#e6e6e6', backgroundColor: '#2a2a2a' } }}
                    InputLabelProps={{ sx: { color: '#e6e6e6' } }}
                  />
                )}
              />
            </div>
            <div className="item">
              <button className="button_shearch" onClick={filterMovies}>Lọc phim</button>
            </div>
          </div>
        </div>

        {/* Danh sách phim */}
        {hasFiltered ? (
          <div className="grid">
            <div className="grid__item grid__item_item4">
              <Grid container spacing={2}>
                {currentMovies.map((movie, index) => (
                  <Grid item xs={3} key={index}>
                    <div className="card" onClick={() => navigate(`/film/${slugify(movie.name)}`, { state: { id: movie.id } })}>
                      <div className="img1">
                        <img src={`data:image/jpeg;base64,${movie.image}`} alt={movie.name} style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: "8px" }} />
                      </div>
                      <div className="title">{movie.name}</div>
                      <div className="text">
                        {movie.style.name === 'Phim bộ' ? `${movie.episodes} Tập Vietsub` : 'Full Vietsub'}
                      </div>
                      <div className="catagory">Phát hành năm: {movie.namphathanh}</div>
                      <div className="icon_video">
                        <PlayCircleRoundedIcon sx={{ width: 60, height: 60 }} />
                      </div>
                      {movie.style.name !== 'Phim bộ' && (
                        <div className="views">{movie.duration} phút</div>
                      )}
                    </div>
                  </Grid>
                ))}
              </Grid>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="pagination">
                  {Array.from({ length: totalPages }, (_, i) => i + 1)
                    .filter(page => page === 1 || page === totalPages || Math.abs(page - currentPage) <= 1)
                    .reduce((acc, page, index, array) => {
                      if (index > 0 && page - array[index - 1] > 1) acc.push('...');
                      acc.push(page);
                      return acc;
                    }, [])
                    .map((item, index) =>
                      item === '...' ? (
                        <span key={`ellipsis-${index}`} className="ellipsis">...</span>
                      ) : (
                        <button key={item} onClick={() => setCurrentPage(item)} className={item === currentPage ? 'active' : ''}>
                          {item}
                        </button>
                      )
                    )}
                </div>
              )}
            </div>
            <div className="grid__item grid__item_item5">
              <ListTrending />
            </div>
          </div>
        ) : (
          <div className="no-data">Vui lòng chọn bộ lọc để tìm phim.</div>
        )}
      </div>
    </div>
  );
}

export default PhimListStyle;
