import { Link } from "react-router-dom";
import NavBar from './navbar';
import HomeIcon from '@mui/icons-material/Home';
import React, { useEffect, useState } from "react";
import axios from 'axios';
import { useParams, useLocation } from "react-router-dom";

function Video() {
    const location = useLocation();
    const id = location.state?.id; // ðŸ‘ˆ Nháº­n láº¡i id tá»« state

    const [movie, setMovie] = useState(null);

    useEffect(() => {
        if (!id) return;

        axios
            .get(`http://localhost:8080/website/movie/${id}`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.error(err));
    }, [id]);

    if (!movie) return <div>Loading...</div>;
    return (
        <div>
            <NavBar />
            <br></br>
            <div className="link">
                <HomeIcon color="primary" sx={{ color: "#8B4513", fontSize: 25, marginLeft: "150px" }} />
                <Link to="/home" className="xemphim">Xem phim</Link> / {movie.style.name} / {movie.name}
            </div>

            <div className="content">

            </div>
        </div>
    );
}

export default Video;
