import ArrowForwardIosRoundedIcon from "@mui/icons-material/ArrowForwardIosRounded";
import ArrowBackIosNewRoundedIcon from "@mui/icons-material/ArrowBackIosNewRounded";
import PlayCircleRoundedIcon from '@mui/icons-material/PlayCircleRounded';
import React, { useEffect, useRef } from "react";

function Content() {
    const carouselRef = useRef(null);

    const handleClickGoAhead = () => {
        const carousel = carouselRef.current;
        if (!carousel) return;

        let nextStep = carousel.offsetWidth;
        const maxScrollLeft = carousel.scrollWidth - carousel.clientWidth;

        if (carousel.scrollLeft + nextStep > maxScrollLeft) {
            nextStep = maxScrollLeft - carousel.scrollLeft;
        }

        carousel.scroll({
            left: carousel.scrollLeft + nextStep,
            behavior: "smooth",
        });
    };

    const handleClickGoBack = () => {
        const carousel = carouselRef.current;
        if (!carousel) return;

        let nextStep = carousel.offsetWidth;
        if (carousel.scrollLeft - nextStep < 0) {
            nextStep = carousel.scrollLeft;
        }

        carousel.scroll({
            left: carousel.scrollLeft - nextStep,
            behavior: "smooth",
        });
    };

    useEffect(() => {
        const cards = document.querySelectorAll(".container");
        cards.forEach((card) => {
            const cardInfo = card.querySelector(".user-info");
            const cardHeading = card.querySelector("h2");

            if (cardInfo && cardHeading) {
                cardInfo.style.transform = `translateY(calc(100% - ${cardHeading.offsetHeight}px))`;

                card.addEventListener("mouseover", () => {
                    cardInfo.style.transform = "translateY(0)";
                });

                card.addEventListener("mouseout", () => {
                    cardInfo.style.transform = `translateY(calc(100% - ${cardHeading.offsetHeight}px))`;
                });
            }
        });
    }, []);

    return (
        <div className="grid">
            <div className="grid__item grid__item_item4">
                <p>Phim đề cử </p><br></br>
                <div id="wrapper">
                    <div id="carousel" ref={carouselRef} >
                        {Array(10)
                            .fill(null)
                            .map((_, index) => (
                                <div className="container" key={index} >
                                    <div className="img-container">
                                        <span className="trending">Tập 12/24</span>
                                        <img
                                            src="https://images.unsplash.com/photo-1534809027769-b00d750a6bac?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1534&q=80"
                                            alt=""
                                        />
                                        <ul class="social-media">
                                            <li><a href="#">PlayCircleRoundedIcon</a></li>
                                            
                                        </ul>
                                    </div>
                                    <div className="user-info">
                                        <h2>Aniket Singh</h2>
                                        <span>20-july</span>
                                    </div>
                                </div>
                            ))}
                    </div>
                </div>
                <div className="buttons">
                    <button onClick={handleClickGoBack}>
                        <ArrowBackIosNewRoundedIcon />
                    </button>
                    <button onClick={handleClickGoAhead}>
                        <ArrowForwardIosRoundedIcon />
                    </button>
                </div>
            </div>
            <div className="grid__item grid__item_item5">four columns</div>
        </div>
    );
}

export default Content;
