import React, { useEffect, useState } from 'react';
import axios from 'axios'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { Paper, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit'




function Type() {
    const [types, setType] = useState(null)
    const [deleteId, setDeleteId] = useState(null)
    const [confirmOpen, setConfirmOpen] = useState(false)
    const [openEdit, setOpenEdit] = useState(false)
    const [snackbarMessage, setSnackbarMessage] = useState("")
    const [snackbarSeverity, setSnackbarSeverity] = useState('success')
    
    
    
    const [newType, setNewType] = useState({
        name: ''
    })
    const [editType, setEditType] = useState({
        name: ''  // Set current datetime
      })
    const handleClickOpenEdit = (product) => {
        setEditProduct(product)
        setOpenEdit(true)
      }
   
    const handleChange = (e) => {
        setNewType({ ...newType, name: e.target.value });
    };
    const handleConfirmOpen = (id) => {
        setDeleteId(id)
        setConfirmOpen(true)
    }
    const handleConfirmClose = (id) => {
        setDeleteId(null)
        setConfirmOpen(false)
    }
    // delete
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/website/type/${id}`);
            setType(types.filter(type => type.id !== id));
            handleConfirmClose()
        } catch (error) {
            alert('There was an error deleting the type')
        }
    };
    // add
    const handleAdd = async () => {
        try {
            const response = await axios.post('http://localhost:8080/website/type', {
                ...newType,
                // createdAt: new Date().toISOString(),  // Set current datetime
            });
            setType([...types, response.data])
            setNewType({
                name: ''
            });
            setSnackbarMessage("Type was added successfully!")
            setSnackbarSeverity("success")
            // handleClose();
        } catch (error) {
            setSnackbarMessage("There was an error adding a Type! Please try again")
            setSnackbarSeverity("warning")
            console.log('There was an error adding the Type!', error)
        }
    }

    useEffect(() => {
        axios.get('http://localhost:8080/website/typeAll').then(response => {
            setType(response.data)

        })
    })
    return (
        <section className="page-content">
            <h1>Type</h1>
            <hr />
            <br />
            <div className="container">
                <div className="row">
                    <div className="col-25">
                        <label htmlFor="fname">Name Type</label>
                    </div>
                    <div className="col-75">
                        <input
                            name="title"
                            type="text"
                            value={newType.name}
                            onChange={handleChange}
                            placeholder="Name Type ..." />
                    </div>
                </div>
                <br></br>
                <div className="row">
                    <Button onClick={handleAdd} color="primary" variant="contained">
                        Add
                    </Button>
                </div>
                <br></br>
                <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 650 }} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell align="center">STT</TableCell>
                                <TableCell align="center">Name Type</TableCell>
                                <TableCell align="center">Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {Array.isArray(types) && types.length > 0 ? (
                                types.map((type, index) => (
                                    <TableRow
                                        key={type.id} // Nên dùng `type.id` thay vì `type.name` nếu ID là duy nhất
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell align="center" component="th" scope="row">
                                            {index + 1}
                                        </TableCell>
                                        <TableCell align="center">{type.name}</TableCell>
                                        <TableCell align="center">
                                            <IconButton color="primary" onClick={() => handleConfirmOpen(type.id)}>
                                                <DeleteIcon />
                                            </IconButton>
                                            <IconButton color="primary" onClick={() => handleClickOpenEdit(type.id)}>
                                                <EditIcon />
                                            </IconButton>
                                        </TableCell>
                                    </TableRow>
                                ))
                            ) : (
                                <TableRow>
                                    <TableCell colSpan={3} align="center">
                                        Loading...
                                    </TableCell>
                                </TableRow>
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>

                {/* Xoa */}
                <Dialog open={confirmOpen} onClose={handleConfirmClose}>
                    <DialogTitle>Confirm DeleteId</DialogTitle>
                    <DialogContent>Are you sure you want to delete this Type?</DialogContent>
                    <DialogActions>
                        <Button onClick={handleConfirmClose} color='primary'>
                            Cancel
                        </Button>
                        <Button onClick={() => { handleDelete(deleteId); }} color='secondary' variant='contained'>
                            Delete
                        </Button>
                    </DialogActions>
                </Dialog>
                {/* edit */}
                <Dialog open={openEdit} onClose={handleCloseEdit}>
                    <DialogTitle>Update Type</DialogTitle>
                    <DialogContent>
                        <TextField
                            margin="dense"
                            name="title"
                            label="Type Title"
                            type="text"
                            fullWidth
                            value={editProduct.title}
                            onChange={handleChangeEdit}
                        />
                        

                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleCloseEdit} color="primary">
                            Cancel
                        </Button>
                        <Button color="primary" variant="contained" onClick={handleEditProduct} >
                            Update 
                        </Button>
                    </DialogActions>
                </Dialog>
            </div>

        </section>
    )
}

export default Type;
